import * as React from "react";
import Table from "@mui/material/Table";
import TableBody from "@mui/material/TableBody";
import TableCell from "@mui/material/TableCell";
import TableContainer from "@mui/material/TableContainer";
import TableHead from "@mui/material/TableHead";
import TableRow from "@mui/material/TableRow";
import Paper from "@mui/material/Paper";
import { useEffect, useState } from "react";
import ls from "local-storage";
import axios from "axios";
import { Button } from "@mui/material";
import Box from "@mui/material/Box";
import Stack from "@mui/material/Stack";
import FormControl from "@mui/material/FormControl";
import Avatar from "@mui/material/Avatar";
import CssBaseline from "@mui/material/CssBaseline";
import TextField from "@mui/material/TextField";
import Grid from "@mui/material/Grid";
import LockOutlinedIcon from "@mui/icons-material/LockOutlined";
import Typography from "@mui/material/Typography";
import Container from "@mui/material/Container";
import { createTheme, ThemeProvider } from "@mui/material/styles";
import { MenuItem } from "@mui/material";
import Select from "@mui/material/Select";
import InputLabel from "@mui/material/InputLabel";

const theme = createTheme();

function Additem() {
  return (
    <ThemeProvider theme={theme}>
      <Container component="main" maxWidth="xs">
        <CssBaseline />
        <Box
          sx={{
            marginTop: 8,
            display: "flex",
            flexDirection: "column",
            alignItems: "center",
          }}
        >
          <Avatar sx={{ m: 1, bgcolor: "secondary.main" }}>
            <LockOutlinedIcon />
          </Avatar>
          <Typography component="h1" variant="h5">
            Register
          </Typography>
          <Box
            component="form"
            noValidate
            // onSubmit={handleRegistration}
            sx={{ mt: 3 }}
          >
            <Grid container spacing={2}>
              <Grid item xs={12}>
                <TextField
                  name="name"
                  required
                  fullWidth
                  id="name"
                  label="Name"
                  autoFocus
                  // onChange={handlename}
                />
              </Grid>
              <Grid item xs={12}>
                <TextField
                  required
                  fullWidth
                  id="email"
                  label="Email Address"
                  name="email"
                  autoComplete="email"
                  // onChange={handleemail}
                  // error={!emailbool}
                  //helperText={!emailbool ? "Enter a valid email" : ""}
                />
              </Grid>
              <Grid item xs={12}>
                <TextField
                  required
                  fullWidth
                  name="password"
                  label="Password"
                  type="password"
                  id="password"
                  autoComplete="new-password"
                  // onChange={handlepassword}
                />
              </Grid>
              <Grid item xs={12}>
                <TextField
                  required
                  fullWidth
                  type="number"
                  name="age"
                  label="Age"
                  id="age"
                  // onChange={handleage}
                />
              </Grid>
              <Grid item xs={12}>
                <FormControl fullWidth>
                  {/* <TextField
                  required
                  fullWidth
                  name="batch"
                  label="Batch"
                  type="batch"
                  id="batch"
                  helperText="Ex. UG1, UG2, PG1"
                /> */}
                  <InputLabel id="batch-select-label" required>
                    Batch
                  </InputLabel>
                  <Select
                    fullWidth
                    labelId="batch-select-label"
                    id="demo-batch-select"
                    name="batch"
                    id="batch"
                    // value={buyer ? 'buyer' : vendor ? 'vendor' : 'Select'}
                    // value={batch}
                    label="Batch"
                    // onChange={handlebatch}
                  >
                    <MenuItem value={0}>UG1</MenuItem>
                    <MenuItem value={1}>UG2</MenuItem>
                    <MenuItem value={2}>UG3</MenuItem>
                    <MenuItem value={3}>UG4</MenuItem>
                    <MenuItem value={4}>UG5</MenuItem>
                    <MenuItem value={5}>PG1</MenuItem>
                    <MenuItem value={6}>PG2</MenuItem>
                  </Select>
                </FormControl>
              </Grid>
              <Grid item xs={12}>
                <TextField
                  required
                  fullWidth
                  type="number"
                  id="contact"
                  label="Contact Number"
                  name="contact"
                  // onChange={handlecontact}
                  // error={!contactbool}
                  // helperText={
                  //   !contactbool ? "Enter a valid contact number" : ""
                  // }
                />
              </Grid>
            </Grid>
            <Button
              type="submit"
              fullWidth
              variant="contained"
              sx={{ mt: 3, mb: 2 }}
            >
              Register
            </Button>
          </Box>
        </Box>
        {/* <Copyright sx={{ mt: 5 }} /> */}
      </Container>
    </ThemeProvider>
  );
}

function createData(ItemName, Price, VNV, Tags, AddOns) {
  return { ItemName, Price, VNV, Tags, AddOns };
}

const initialrows = [
  createData("Frozen yoghurt", 159, 6.0, "Me", 24),
  createData("Frozen yoghurt", 159, 6.0, "Me", 24),
];

const initialrows2 = [
  createData("", 0, 0.0, "", 0),
  createData("", 0, 0.0, "", 0),
];

export default function ShowMenu( props ) {
  const [rows, setRows] = useState(props ? initialrows2 : initialrows);
  const [additem, setAddItem] = useState(props ? true : false);
  // Fill up the rows array with data.
  useEffect(() => {
    // Get all documents from the database for orders for the particular vendor
    axios
      .post("http://localhost:4000/vendor/fooditems", {
        manangeremail: ls.get("email"),
      })
      .then(function (response) {
        console.log(response);
        setRows(
          response.data.map((item) =>
            createData(
              item.ItemName,
              item.Price,
              item.VNV,
              item.Tags,
              item.AddOns
            )
          )
        );
      })
      .catch(function (error) {
        console.log(error);
      });
  });

  // const handleadditem = () => {
  // }

  return (
    <Box>
      <FormControl fullWidth>
        <Stack
          sx={{ pt: 4 }}
          direction="row"
          spacing={2}
          justifyContent="center"
        >
          <Button variant="contained" onClick={setAddItem(true)}>
            Add Item
          </Button>
        </Stack>
      </FormControl>
      <Box>{additem ? Additem() : <></>}</Box>

      <TableContainer component={Paper}>
        <Table sx={{ minWidth: 650 }} aria-label="simple table">
          <TableHead>
            <TableRow>
              <TableCell>ItemName</TableCell>
              <TableCell align="right">Price</TableCell>
              <TableCell align="right">Veg/Non-Veg</TableCell>
              <TableCell align="right">Tags</TableCell>
              <TableCell align="right">AddOns</TableCell>
              <TableCell align="right"></TableCell>
              <TableCell align="right"></TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {rows.map((row) => (
              <TableRow
                key={row.name}
                sx={{ "&:last-child td, &:last-child th": { border: 0 } }}
              >
                <TableCell component="th" scope="row">
                  {row.ItemName}
                </TableCell>
                <TableCell align="right">{row.Price}</TableCell>
                <TableCell align="right">{row.VNV}</TableCell>
                <TableCell align="right">{row.Tags}</TableCell>
                <TableCell align="right">{row.AddOns}</TableCell>
                <TableCell align="right">
                  <Button variant="contained">Edit</Button>
                </TableCell>

                <TableCell align="right">
                  <Button variant="contained">Delete</Button>
                </TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>
    </Box>
  );
}
