import * as React from "react";
import Table from "@mui/material/Table";
import TableBody from "@mui/material/TableBody";
import TableCell from "@mui/material/TableCell";
import TableContainer from "@mui/material/TableContainer";
import TableHead from "@mui/material/TableHead";
import TableRow from "@mui/material/TableRow";
import Paper from "@mui/material/Paper";
import { useEffect, useState } from "react";
import ls from "local-storage";
import axios from "axios";
import { Button } from "@mui/material";
import Box from "@mui/material/Box";
import Stack from "@mui/material/Stack";
import FormControl from "@mui/material/FormControl";
import Adding from "./AddItem";

function createData(_id ,ItemName, Price, VNV, Tags, AddOnName, AddOnPrice) {
  return { _id, ItemName, Price, VNV, Tags, AddOnName, AddOnPrice };
}

const initialrows = [
  createData('adsd', "Frozen yoghurt", 159, true, "Me", 24, 9),
  createData('sdfsg', "Frozen yoghurt", 159, false, "Me", 24, 7),
];

export default function ShowMenu() {
  const [rows, setRows] = useState(initialrows);
  const [additem, setAddItem] = useState(false);
  // Fill up the rows array with data.
  useEffect(() => {
    // Get all documents from the database for orders for the particular vendor
    axios
      .post("http://localhost:4000/vendor/fooditems", {
        manangeremail: ls.get("email"),
      })
      .then(function (response) {
        console.log(response);
        setRows(response.data);
      })
      .catch(function (error) {
        console.log(error);
      });
  });

  // const handleadditem = () => {
  // }

  return (
    <div>
      <Box
        display="flex"
        justifyContent="center"
        alignItems="center"
        flexDirection="column"
      >
        <Stack spacing={2}>
          <Button
            variant="contained"
            color="primary"
            onClick={() => setAddItem(true)}
          >
            Add Item
          </Button>
        </Stack>
        </Box>
        {additem ? <Adding /> : null}
      <TableContainer component={Paper}>
        <Table sx={{ minWidth: 650 }} aria-label="simple table">
          <TableHead>
            <TableRow>
              <TableCell>ItemName</TableCell>
              <TableCell align="right">Price</TableCell>
              <TableCell align="right">Veg/Non-Veg</TableCell>
              <TableCell align="right">Tags</TableCell>
              <TableCell align="right">AddOns</TableCell>
              <TableCell align="right"></TableCell>
              <TableCell align="right"></TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {rows.map((row) => (
              <TableRow
                key={row._id}
                sx={{ "&:last-child td, &:last-child th": { border: 0 } }}
              >
                <TableCell component="th" scope="row">
                  {row.ItemName}
                </TableCell>
                <TableCell align="right">{row.Price}</TableCell>
                <TableCell align="right">
                  {row.VNV === true ? "Veg" : "Non-Veg"}
                </TableCell>
                <TableCell align="right">{row.Tags}</TableCell>
                <TableCell align="right">
                  {row.AddOnName}:
                  {row.AddOnPrice}
                </TableCell>
                <TableCell align="right">
                  <Button variant="contained">Edit</Button>
                </TableCell>
                <TableCell align="right">
                  <Button variant="contained">Delete</Button>
                </TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>
    </div>
  );
}
