import * as React from "react";
import Table from "@mui/material/Table";
import TableBody from "@mui/material/TableBody";
import TableCell from "@mui/material/TableCell";
import TableContainer from "@mui/material/TableContainer";
import TableHead from "@mui/material/TableHead";
import TableRow from "@mui/material/TableRow";
import Paper from "@mui/material/Paper";
import { useEffect, useState } from "react";
import ls from "local-storage";
import axios from "axios";
import { Button } from "@mui/material";
import Box from "@mui/material/Box";
import Stack from "@mui/material/Stack";
import FormControl from "@mui/material/FormControl";

function createData(ItemName, Price, VNV, Tags, AddOns) {
  return { ItemName, Price, VNV, Tags, AddOns };
}

const initialrows = [
  createData("Frozen yoghurt", 159, 6.0, "Me", 24),
  createData("Frozen yoghurt", 159, 6.0, "Me", 24),
];

export default function ShowMenu() {
  const [rows, setRows] = useState(initialrows);
  const [additem, setAddItem] = useState(false);
  // Fill up the rows array with data.
  useEffect(() => {
    // Get all documents from the database for orders for the particular vendor
    axios
      .post("http://localhost:4000/vendor/fooditems", {
        manangeremail: ls.get("email"),
      })
      .then(function (response) {
        console.log(response);
        setRows(
          response.data.map((item) =>
            createData(
              item._id,
              item.ItemName,
              item.Price,
              item.VNV,
              item.Tags,
              item.AddOns
            )
          )
        );
      })
      .catch(function (error) {
        console.log(error);
      });
  });

  // const handleadditem = () => {
  // }

  return (
    <Box>
      <FormControl fullWidth>
        <Stack
          sx={{ pt: 4 }}
          direction="row"
          spacing={2}
          justifyContent="center"
        >
          <Button variant="contained" onClick={setAddItem(true)}>
            Add Item
          </Button>
        </Stack>
      </FormControl>

      <TableContainer component={Paper}>
        <Table sx={{ minWidth: 650 }} aria-label="simple table">
          <TableHead>
            <TableRow>
              <TableCell>ItemName</TableCell>
              <TableCell align="right">Price</TableCell>
              <TableCell align="right">Veg/Non-Veg</TableCell>
              <TableCell align="right">Tags</TableCell>
              <TableCell align="right">AddOns</TableCell>
              <TableCell align="right"></TableCell>
              <TableCell align="right"></TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {rows.map((row) => (
              <TableRow
                key={row._id}
                sx={{ "&:last-child td, &:last-child th": { border: 0 } }}
              >
                <TableCell component="th" scope="row">
                  {row.ItemName}
                </TableCell>
                <TableCell align="right">{row.Price}</TableCell>
                <TableCell align="right">{row.VNV}</TableCell>
                <TableCell align="right">{row.Tags}</TableCell>
                <TableCell align="right">{row.AddOns}</TableCell>
                <TableCell align="right">
                  <Button variant="contained">Edit</Button>
                </TableCell>

                <TableCell align="right">
                  <Button variant="contained">Delete</Button>
                </TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>
    </Box>
  );
}
