import * as React from "react";
import Table from "@mui/material/Table";
import TableBody from "@mui/material/TableBody";
import TableCell from "@mui/material/TableCell";
import TableContainer from "@mui/material/TableContainer";
import TableHead from "@mui/material/TableHead";
import TableRow from "@mui/material/TableRow";
import Paper from "@mui/material/Paper";
import { useEffect, useState } from "react";
import ls from "local-storage";
import axios from "axios";
import { Button } from "@mui/material";
import Box from "@mui/material/Box";
import Stack from "@mui/material/Stack";
import InputLabel from "@mui/material/InputLabel";
import MenuItem from "@mui/material/MenuItem";
import FormControl from "@mui/material/FormControl";
import Select from "@mui/material/Select";
import { createTheme } from "@mui/material/styles";
import { deepOrange } from "@mui/material/colors";
import { yellow } from "@mui/material/colors";
import Chip from "@mui/material/Chip";

const ITEM_HEIGHT = 48;
const ITEM_PADDING_TOP = 8;
const MenuProps = {
  PaperProps: {
    style: {
      maxHeight: ITEM_HEIGHT * 4.5 + ITEM_PADDING_TOP,
      width: 250,
    },
  },
};

function getStyles(name, nameselected, theme) {
  return {
    fontWeight:
      nameselected.indexOf(name) === -1
        ? theme.typography.fontWeightRegular
        : theme.typography.fontWeightMedium,
  };
}

export default function Myitem() {
  const [nameselected, setNameselected] = React.useState([]);
  const [menu, setMenu] = useState([]);
  const [sortedmenu, setSortedMenu] = useState([]);
  const [refresh, setRefresh] = useState(false);

  const handleChange = (event) => {
    const {
      target: { value },
    } = event;
    setNameselected(
      // On autofill we get a stringified value.
      typeof value === 'string' ? value.split(',') : value,
    );
  };

  // Fill up the rows array with data.
  useEffect(() => {
    // Get all documents from the database for item for the particular vendor
    axios
      .post("http://localhost:4000/buyer/showmenu")
      .then((res) => {
        setMenu(res.data);
        setSortedMenu(res.data);
      })
      .catch((err) => {
        console.log(err);
      });
  }, [refresh]);

  return (
    //Favourites

    //Filters

    //Menu
    <TableContainer component={Paper}>
      <Table sx={{ minWidth: 650 }} aria-label="simple table">
        <TableHead>
          <TableRow>
            <TableCell>ItemName</TableCell>
            <TableCell align="center">Image</TableCell>
            <TableCell align="center">ShopName</TableCell>
            <TableCell align="center">Price</TableCell>
            <TableCell align="center">Veg/Non-Veg</TableCell>
            <TableCell align="center">Tags</TableCell>
            {/* Buying Stages */}
            <TableCell align="center">AddOns</TableCell>
            <TableCell align="center">Quantity</TableCell>
            <TableCell align="center"></TableCell>
          </TableRow>
        </TableHead>
        <TableBody>
          {menu.map((row) => (
            <TableRow
              key={row._id}
              sx={{ "&:last-child td, &:last-child th": { border: 0 } }}
            >
              <TableCell component="th" scope="row">
                {row.itemname}
              </TableCell>
              <TableCell align="center">
                <img
                  src={row.image}
                  alt="food"
                  style={{ width: "120px", height: "100px" }}
                />
              </TableCell>
              <TableCell align="center">{row.shopname}</TableCell>
              <TableCell align="center">Rs.{row.price}</TableCell>
              <TableCell align="center">
                {row.veg === true ? "Veg" : "Non-Veg"}
              </TableCell>
              <TableCell align="center">
                {/* <FormControl fullWidth>
                    <InputLabel id="demo-simple-select-label">List</InputLabel>
                    <Select
                      labelId="demo-simple-select-label"
                      id="demo-simple-select"
                      label="List"
                    >
                      {row.tags.map((tag) => (
                        <MenuItem key={tag}>{tag}</MenuItem>
                      ))}
                    </Select>
                  </FormControl> */}
                {row.tags.map((tag) => (

                  <div spacing={2} key={tag}>
                    <Stack spacing={2}>
                    <Chip label={tag}
                    key={tag}
                    />
                    </Stack>
                  </div>
                ))}
              </TableCell>
              <TableCell align="center">
                {/* <FormControl fullWidth>
                  <InputLabel id="demo-simple-select-label">List</InputLabel>
                  <Select
                    labelId="demo-simple-select-label"
                    id="demo-simple-select"
                    // value={row.addonname}
                    label="List"
                  >
                    //map addonname and addonprice simultaneously
                    {row.addonname.map((addon, index) => (
                      <MenuItem key={index}>
                        {addon}: Rs.
                        {row.addonprice[index]}
                      </MenuItem>
                    ))}
                  </Select>
                </FormControl> */}
                <div>
                <FormControl sx={{ m: 1, width: 300 }}>
                  <Select
                    labelId="demo-multiple-chip-label"
                    id="addon"
                    multiple
                    value={addon}
                    onChange={handleChange}
                    input={<OutlinedInput id="select-multiple-chip" label="Chip" />}
                    renderValue={(selected) => (
                      <Box sx={{ display: 'flex', flexWrap: 'wrap', gap: 0.5 }}>
                        {selected.map((value) => (
                          <Chip key={value} label={value} />
                        ))}
                      </Box>
                    )}
                    MenuProps={MenuProps}
                  >
                    {/* {names.map((name) => (
                      <MenuItem
                        key={name}
                        value={name}
                        style={getStyles(name, nameselected, theme)}
                      >
                        {name}
                      </MenuItem>
                    ))} */}
                    {row.addonname.map((addon, index) => (
                      <MenuItem key={addon} value={addon}>
                        {addon}: Rs. {row.addonprice[index]}
                      </MenuItem>
                    ))}   
                  </Select>
                </FormControl>
              </div>
              </TableCell>
              <TableCell align="center">
                {/* Edit row */}
                <Button
                  variant="contained"
                  color="primary"
                  // pass current row to handleedit
                  // onClick={() => handleedit(row._id)}
                >
                  Edit
                </Button>
              </TableCell>
              <TableCell align="center">
                <Button
                  variant="contained"
                  color="secondary"
                  //handledelete
                  // onClick={() => handledelete(row._id)}
                >
                  Delete
                </Button>
              </TableCell>
            </TableRow>
          ))}
        </TableBody>
      </Table>
    </TableContainer>
  );
}
