import * as React from "react";
import Table from "@mui/material/Table";
import TableBody from "@mui/material/TableBody";
import TableCell from "@mui/material/TableCell";
import TableContainer from "@mui/material/TableContainer";
import TableHead from "@mui/material/TableHead";
import TableRow from "@mui/material/TableRow";
import Paper from "@mui/material/Paper";
import { useEffect, useState } from "react";
import ls, { set } from "local-storage";
import axios from "axios";
import { Button } from "@mui/material";
import Rating from "@mui/material/Rating";

export default function Myitem() {
  const [menu, setMenu] = useState([]);
  const [sortedmenu, setSortedMenu] = useState([]);
  const [refresh, setRefresh] = useState(false);

  // Fill up the rows array with data.
  useEffect(() => {
    // Get all documents from the database for item for the particular vendor
    axios
      .post("http://localhost:4000/buyer/showmenu")
      .then((res) => {
        setMenu(res.data);
        setSortedMenu(res.data);
      })
      .catch((err) => {
        console.log(err);
      });
  }, [refresh]);

  const handlenextstage = (props) => {
    const _id = props;

    axios
      .post("http://localhost:4000/buyer/pickuporder", {
        _id: _id,
      })
      .then((response) => {
        console.log(response);
      })
      .catch((error) => {
        console.log(error);
      });
  };

  const handlerating = (a, b) => {
    setRatingbool(true);
    // console.log(rating);
    // console.log(document.getElementById("rating").value);
    axios
      .post("http://localhost:4000/buyer/ratethefood", {
        _id: a,
        rating: b,
      })
      .then((response) => {
        console.log(response);
      })
      .catch((error) => {
        console.log(error);
      });
  };

  return (
    //Favourites

    //Filters

    //Menu
    <TableContainer component={Paper}>
        <Table sx={{ minWidth: 650 }} aria-label="simple table">
          <TableHead>
            <TableRow>
              <TableCell>ItemName</TableCell>
              <TableCell align="center">Image</TableCell>
              <TableCell align="center">ShopName</TableCell>
              <TableCell align="center">Price</TableCell>
              <TableCell align="center">Veg/Non-Veg</TableCell>
              <TableCell align="center">Tags</TableCell>
              {/* Buying Stages */}
              <TableCell align="center">AddOns</TableCell>
              <TableCell align="center">Quantity</TableCell>
              <TableCell align="center"></TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {menu.map((row) => (
              <TableRow
                key={row._id}
                sx={{ "&:last-child td, &:last-child th": { border: 0 } }}
              >
                <TableCell component="th" scope="row">
                  {row.itemname}
                </TableCell>
                <TableCell align="center">
                  <img
                    src={row.image}
                    alt="food"
                    style={{ width: "120px", height: "100px" }}
                  />
                </TableCell>
                <TableCell align="center">{row.shopname}</TableCell>
                <TableCell align="center">Rs.{row.price}</TableCell>
                <TableCell align="center">
                  {row.veg === true ? "Veg" : "Non-Veg"}
                </TableCell>
                <TableCell align="center">
                <FormControl fullWidth>
                    <InputLabel id="demo-simple-select-label">List</InputLabel>
                    <Select
                      labelId="demo-simple-select-label"
                      id="demo-simple-select"
                      label="List"
                    >
                      {row.tags.map((tag) => (
                        <MenuItem key={tag}>{tag}</MenuItem>
                      ))}
                    </Select>
                  </FormControl>
                </TableCell>
                <TableCell align="center">
                  <FormControl fullWidth>
                    <InputLabel id="demo-simple-select-label">List</InputLabel>
                    <Select
                      labelId="demo-simple-select-label"
                      id="demo-simple-select"
                      // value={row.addonname}
                      label="List"
                    >
                      {/* //map addonname and addonprice simultaneously */}
                      {row.addonname.map((addon, index) => (
                        <MenuItem key={index}>
                          {addon}: Rs.
                          {row.addonprice[index]}
                        </MenuItem>
                      ))}
                    </Select>
                  </FormControl>
                </TableCell>
                <TableCell align="center">
                  {/* Edit row */}
                  <Button
                    variant="contained"
                    color="primary"
                    // pass current row to handleedit
                    // onClick={() => handleedit(row._id)}                     
                  >
                    Edit
                  </Button>
                </TableCell>
                <TableCell align="center">
                  <Button variant="contained"
                    color="secondary"
                    //handledelete
                    // onClick={() => handledelete(row._id)}
                  >
                    Delete
                  </Button>
                </TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>
  );
}
