import * as React from "react";
import Table from "@mui/material/Table";
import TableBody from "@mui/material/TableBody";
import TableCell from "@mui/material/TableCell";
import TableContainer from "@mui/material/TableContainer";
import TableHead from "@mui/material/TableHead";
import TableRow from "@mui/material/TableRow";
import Paper from "@mui/material/Paper";
import { useEffect, useState } from "react";
import ls, { set } from "local-storage";
import axios from "axios";
import { Button } from "@mui/material";
import Box from "@mui/material/Box";
import Stack from "@mui/material/Stack";
import InputLabel from "@mui/material/InputLabel";
import MenuItem from "@mui/material/MenuItem";
import FormControl from "@mui/material/FormControl";
import Select from "@mui/material/Select";
import { createTheme } from "@mui/material/styles";
import { deepOrange } from "@mui/material/colors";
import { yellow } from "@mui/material/colors";
import Chip from "@mui/material/Chip";
import OutlinedInput from "@mui/material/OutlinedInput";
import { TextField } from "@mui/material";
import PropTypes from "prop-types";
import DialogTitle from "@mui/material/DialogTitle";
import Dialog from "@mui/material/Dialog";
import Checkbox from "@mui/material/Checkbox";
import FormGroup from "@mui/material/FormGroup";
import FormControlLabel from "@mui/material/FormControlLabel";
import Rating from "@mui/material/Rating";

SimpleDialog.propTypes = {
  onClose: PropTypes.func.isRequired,
  open: PropTypes.bool.isRequired,
  selectedValue: PropTypes.string.isRequired,
};

function Print() {
  return <div>Meh...</div>;
}
var array1 = [];
var array2 = [];
var array3 = [];

export default function Myitem() {
  const [menu, setMenu] = useState([]);
  const [sortedmenu, setSortedMenu] = useState([]);
  const [menuaftersearch, setMenuaftersearch] = useState([]);
  const [menuafterveg, setMenuafterveg] = useState([]);
  const [menuafterprice, setMenuafterprice] = useState([]);
  const [refresh, setRefresh] = useState(false);

  const [openshops, setOpenshops] = useState([]);
  const [closedshops, setClosedshops] = useState([]);

  const [search, setSearch] = useState("");
  const [minprice, setMinPrice] = useState(0);
  const [maxprice, setMaxPrice] = useState(1000);

  const [open, setOpen] = useState(false);
  const [selectedValue, setSelectedValue] = useState("");
  const [vegtrue, setVegtrue] = useState(true);
  const [nonvegtrue, setNonvegtrue] = useState(true);

  const [shopnames, setShopnames] = useState([]);
  const [tags, setTags] = useState([]);
  const [selectedshopnames, setSelectedshopnames] = useState([]);
  const [selectedtags, setSelectedtags] = useState([]);

  const [wallet, setWallet] = useState(0);

  const [meh, setMeh] = useState(false);

  const handleClickOpen = (props) => {
    // console.log(props);
    //set selected value as props
    setSelectedValue(props);
    console.log(selectedValue);
    setOpen(true);
  };

  const handleClose = (value) => {
    setOpen(false);
    setSelectedValue(value);
  };

  // Fill up the rows array with data.
  useEffect(() => {
    // Get all documents from the database for item for the particular vendor
    //Get time string in HH:MM format
    var today = new Date();
    var time = today.getHours() + ":" + today.getMinutes();
    console.log(time);
    axios
      .post("http://localhost:4000/buyer/showmenu")
      .then((res) => {
        setMenu(res.data);
        array1 = res.data;
        array2 = res.data;
        array3 = res.data;
        const time = new Date();
        //hours in 24 hrs format
        let hours = time.getHours();
        //minutes
        let minutes = time.getMinutes();
        setSortedMenu(res.data);
        //All items
        setShopnames(res.data.map((item) => item.shopname));
        //Remove duplicates
        const shopnames = [...new Set(res.data.map((item) => item.shopname))];
        setShopnames(shopnames);
        //List of tags
        var arrtags = [];
        res.data.forEach((item) => {
          for (var i = 0; i < item.tags.length; i++) {
            arrtags.push(item.tags[i]);
          }
        });
        //Remove duplicates from tags
        const tags = [...new Set(arrtags)];
        setTags(tags);
      })
      .catch((err) => {
        console.log(err);
      });

    axios
      .post("http://localhost:4000/buyer/walletdetails", {
        email: ls.get("email"),
      })
      .then((res) => {
        setWallet(res.data.wallet);
        ls.set("wallet", res.data.wallet);
      })
      .catch((err) => {
        console.log(err);
      });

    //If time is between openingtime and closingtime of the shop, then the shop is open
    // setOpenshops(menu.filter(item => item.openingtime <= time && item.closingtime >= time));
    //If time is not between openingtime and closingtime of the shop, then the shop is closed
    // setClosedshops(menu.filter(item => item.openingtime > time || item.closingtime < time));
      handlefilter();

  }, [refresh]);

  useEffect(() => {
    setSortedMenu(sortedmenu);
  }, [sortedmenu]);

  const handlefavourite = (key) => {
    //add to favourites
    axios
      .post("http://localhost:4000/buyer/addfavourite", {
        _id: key,
        email: ls.get("email"),
      })
      .then((res) => {
        console.log(res);
      })
      .catch((err) => {
        console.log(err);
      });
  };

  // const handlecheckboxes = (prop, bool) => {
  //   if (prop === "veg") {
  //     setVegtrue(bool);
  //   } else if (prop === "nonveg") {
  //     setNonvegtrue(bool);
  //   }
  //   setMenuafterveg(menuaftersearch);
  //   setMenuafterprice(menuaftersearch);
  //   if (prop === "veg" && bool === true) {
  //     if (nonvegtrue === false) {
  //       setMenuafterveg(menuaftersearch.filter((item) => item.veg === true));
  //     }
  //   }
  //   if (prop === "nonveg" && bool === true) {
  //     if (vegtrue === false) {
  //       setMenuafterveg(menuaftersearch.filter((item) => item.veg === false));
  //     }
  //   }
  //   if (prop === "veg" && bool === false) {
  //     if (nonvegtrue === true) {
  //       setMenuafterveg(menuaftersearch.filter((item) => item.veg === false));
  //     }
  //   }
  //   if (prop === "nonveg" && bool === false) {
  //     if (vegtrue === true) {
  //       setMenuafterveg(menuaftersearch.filter((item) => item.veg === true));
  //     }
  //   }
  //   setMenuafterprice(menuafterveg);
  // };

  const handlefilter = () => {
    //filter according to search keyword irrespective of case
    // setSortedMenu(menu);
    // setSortedMenu(menu);
    array2 = array1;
    if (search !== "") {
      array2 = array2.filter((item) =>
        item.itemname.toLowerCase().includes(search.toLowerCase())
      );
    }

    //filter according to veg and nonveg
    if (vegtrue === true && nonvegtrue === false) {
      array2 = array2.filter((item) => item.veg === true);
    } else if (vegtrue === false && nonvegtrue === true) {
      array2 = array2.filter((item) => item.veg === false);
    }
    // //filter according to price
    // if (minprice !== "" && maxprice !== "") {
    //   setSortedMenu(
    //     sortedmenu.filter(
    //       (item) => item.price >= minprice && item.price <= maxprice
    //     )
    //   );
    // } else if (minprice !== "") {
    //   setSortedMenu(sortedmenu.filter((item) => item.price >= minprice));
    // } else if (maxprice !== "") {
    //   setSortedMenu(sortedmenu.filter((item) => item.price <= maxprice));
    // }
    // else if(minprice === "" && maxprice === "")
    // {
    //   setSortedMenu(sortedmenu.filter((item) => item.price >= 0 && item.price <= 10000));
    // }
    // //filter according to shopname
    // //filter accoring to tags

    setSortedMenu(array2);
  };

  return (
    <div>
      {/* Filters */}
      <div>
        <Box
          display="flex"
          flexDirection="row"
          alignItems="right"
          justifyContent="right"
          p={1}
          m={1}
          css={{ maxWidth: "100%" }}
        >
          <TextField
            id="outlined-basic"
            label="Wallet"
            variant="filled"
            value={wallet}
            InputProps={{
              readOnly: true,
            }}
          />
        </Box>
      </div>
      <div>
        <Box
          component="form"
          sx={{
            "& > :not(style)": { m: 1, width: "25ch" },
          }}
          noValidate
          autoComplete="off"
        >
          <TextField
            id="outlined-basic"
            label="Search"
            variant="outlined"
            //Search for items which includes the word and refresh the menu
            //lowercase
            onChange={(e) => {
              setSearch(e.target.value);
            }}
          />

          <FormGroup>
            <FormControlLabel
              control={
                <Checkbox
                  checked={vegtrue}
                  label="Veg"
                  //pass checkbox value to handlecheckboxes
                  onChange={(e) => {
                    setVegtrue(e.target.checked);
                  }}
                  inputProps={{ "aria-label": "primary checkbox" }}
                />
              }
              label="Veg"
            />
            <FormControlLabel
              control={
                <Checkbox
                  checked={nonvegtrue}
                  label="Non-Veg"
                  onChange={(e) => {
                    setNonvegtrue(e.target.checked);
                  }}
                  inputProps={{ "aria-label": "primary checkbox" }}
                />
              }
              label="Non-Veg"
            />
          </FormGroup>
        </Box>
        <br />
        {/* Price range having minimum and maximum values */}
        <Box
          component="form"
          sx={{
            "& > :not(style)": { m: 1, width: "25ch", height: "7ch" },
          }}
          noValidate
          autoComplete="off"
        >
          <TextField
            id="outlined-basic"
            label="Minimum Price"
            variant="outlined"
            type="number"
            onChange={(e) => {
              setMinPrice(e.target.value);
            }}
          />
          <TextField
            id="outlined-basic"
            label="Maximum Price"
            variant="outlined"
            type="number"
            onChange={(e) => {
              setMaxPrice(e.target.value);
            }}
          />
          {/* <Box
          component="form"
          sx={{
            "& > :not(style)": { m: 1, width: "25ch" },
          }}
          noValidate
          autoComplete="off"
          >
          <Button
            variant="contained"
            color="primary"
            onClick={() => {
              setMenuafterprice(
                menuafterveg.filter((item) => {
                  return item.price >= minprice && item.price <= maxprice;
                })
              );
            }}
          >
            Apply
          </Button>
        </Box> */}
        </Box>
        {/* <br /> */}
        <Box
          component="form"
          sx={{
            "& > :not(style)": { m: 1, width: "25ch" },
          }}
          noValidate
          autoComplete="off"
        >
          {/* Create multiselect for selecting shops */}
          <FormControl variant="outlined">
            <InputLabel id="demo-simple-select-outlined-label">
              Select Shop
            </InputLabel>
            <Select
              labelId="demo-simple-select-outlined-label"
              id="demo-simple-select-outlined"
              defaultValue={''}
              value={selectedshopnames}
              onChange={(e) => {
                setSelectedshopnames(e.target.value);
              }}
              label="Select Shop"
            >
              {shopnames.map((item) => {
                return (
                  <MenuItem key={item} value={item}>
                    {item}
                  </MenuItem>
                );
              })}
            </Select>
          </FormControl>
        {/* </Box>
        <Box
          component="form"
          sx={{
            "& > :not(style)": { m: 1, width: "25ch" },
          }}
          noValidate
          autoComplete="off"
        > */}
          {/* Create multiselect for selecting tags */}
          <FormControl variant="outlined">
            <InputLabel id="demo-simple-select-outlined-label">
              Select Tags
            </InputLabel>
            <Select
              labelId="demo-simple-select-outlined-label"
              id="demo-simple-select-outlined"
              defaultValue={''}
              value={selectedtags}
              onChange={(e) => {
                setSelectedtags(e.target.value);
              }}
              label="Select Tags"
            >
              {tags.map((item) => {
                return (
                  <MenuItem key={item} value={item}>
                    {item}
                  </MenuItem>
                );
              })}
            </Select>
          </FormControl>
        </Box>
        <Box
          component="form"
          sx={{
            "& > :not(style)": { m: 1, width: "25ch", height: "7ch" },
          }}
          noValidate
          autoComplete="off"
        >
          <Button
            variant="contained"
            color="secondary"
            onClick={() => {
              handlefilter();
            }}
          >
            Filter
          </Button>
        </Box>
        <Box
          component="form"
          sx={{
            "& > :not(style)": { m: 1, width: "25ch", height: "7ch" },
          }}
          noValidate
          autoComplete="off"
        >
          {/* Ascending by price button */}
          <Button
            variant="contained"
            color="primary"
            onClick={() => {
              setSortedMenu(
                sortedmenu.sort((a, b) => (a.price > b.price ? 1 : -1))
              );
            }}
          >
            Ascending by Price
          </Button>
          {/* Descending by price button */}
          <Button
            variant="contained"
            color="primary"
            onClick={() => {
              setSortedMenu(
                sortedmenu.sort((a, b) => (a.price > b.price ? -1 : 1))
              );
            }}
          >
            Descending by Price
          </Button>
          {/* Ascending by rating */}
          <Button
            variant="contained"
            color="primary"
            onClick={() => {
              setSortedMenu(
                sortedmenu.sort((a, b) => {
                  return a.rating - b.rating;
                })
              );
            }}
          >
            Ascending by Rating
          </Button>
          {/* Descending by rating */}
          <Button
            variant="contained"
            color="primary"
            onClick={() => {
              setSortedMenu(
                sortedmenu.sort((a, b) => {
                  return b.rating - a.rating;
                })
              );
            }}
          >
            Descending by Rating
          </Button>
        </Box>
      </div>

      {/* Menu */}
      <Box>
        <TableContainer component={Paper}>
          <Table sx={{ minWidth: 650 }} aria-label="simple table">
            <TableHead>
              <TableRow>
                <TableCell>ItemName</TableCell>
                <TableCell align="center">Image</TableCell>
                <TableCell align="center">ShopName</TableCell>
                <TableCell align="center">Price</TableCell>
                <TableCell align="center">Veg/Non-Veg</TableCell>
                <TableCell align="center">Tags</TableCell>
                {/* Buying Stages */}
                <TableCell align="center">AddOns</TableCell>
                <TableCell align="center">Rating</TableCell>
                <TableCell align="center"></TableCell>
                <TableCell align="center"></TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {sortedmenu.map((row) => (
                <TableRow
                  key={row._id}
                  sx={{ "&:last-child td, &:last-child th": { border: 0 } }}
                >
                  <TableCell component="th" scope="row">
                    {row.itemname}
                  </TableCell>
                  <TableCell align="center">
                    <img
                      src={row.image}
                      alt="food"
                      style={{ width: "120px", height: "100px" }}
                    />
                  </TableCell>
                  <TableCell align="center">{row.shopname}</TableCell>
                  <TableCell align="center">Rs.{row.price}</TableCell>
                  <TableCell align="center">
                    {row.veg === true ? "Veg" : "Non-Veg"}
                  </TableCell>
                  <TableCell align="center">
                    {row.tags.map((tag) => (
                      <div spacing={2} key={tag}>
                        <Stack spacing={2}>
                          <Chip label={tag} key={tag} />
                        </Stack>
                      </div>
                    ))}
                  </TableCell>
                  <TableCell align="center">
                    {row.addonname.map((addon, index) => (
                      <div spacing={2} key={index}>
                        <Stack spacing={2}>
                          {/* Addonname and addonprice */}
                          <Chip
                            label={`${addon}: Rs.${row.addonprice[index]}`}
                            key={index}
                          />
                        </Stack>
                      </div>
                    ))}
                  </TableCell>
                  <TableCell align="center">
                    {/* Rating */}
                    <Rating
                      name="read-only"
                      value={row.rating}
                      readOnly
                      size="small"
                    />
                  </TableCell>
                  <TableCell align="center">
                    <Button
                      variant="contained"
                      color="secondary"
                      onClick={() => handlefavourite(row._id)}
                    >
                      Add to Favourites
                    </Button>
                  </TableCell>
                  <TableCell align="center">
                    <Button
                      variant="contained"
                      color="secondary"
                      onClick={() => handleClickOpen(row._id)}
                    >
                      Buy
                    </Button>
                  </TableCell>
                </TableRow>
              ))}
            </TableBody>
          </Table>
        </TableContainer>
        {open ? (
          <SimpleDialog
            selectedValue={selectedValue}
            open={open}
            onClose={handleClose}
          />
        ) : null}
      </Box>
    </div>
  );
}

function SimpleDialog(props) {
  const { onClose, selectedValue, open } = props;
  const [quantity, setQuantity] = useState(1);
  const [itemname, setItemname] = useState("");
  const [shopname, setShopname] = useState("");
  const [vendoremail, setVendoremail] = useState("");
  const [price, setPrice] = useState(0);
  const [addonname, setAddonname] = useState([]);
  const [addonprice, setAddonprice] = useState([]);
  const [selectedaddonname, setSelectedaddonname] = useState([]);
  const [selectedaddonprice, setSelectedaddonprice] = useState([]);
  const [totalprice, setTotalprice] = useState(0);
  const handleClose = () => {
    onClose();
  };
  const handlebuy = () => {
    //get time in int
    const time = new Date().getTime();
    // convert quantity to int
    const q = parseInt(quantity);
    var p = parseInt(price);
    // run loop on addonprice
    for (let i = 0; i < selectedaddonprice.length; i++) {
      // convert addonprice to int
      const ap = parseInt(selectedaddonprice[i]);
      // add addonprice to p
      p = p + ap;
    }
    // add quantity to p
    p = p * q;
    if (p > ls.get("wallet")) {
      alert("Insufficient Balance");
      onClose();
    } else {
      console.log({
        itemname: itemname,
        shopname: shopname,
        price: price,
        addonname: selectedaddonname,
        addonprice: selectedaddonprice,
        quantity: q,
        totalprice: p,
        time: time,
      });
      axios
        .post("http://localhost:4000/buyer/orderitem", {
          itemname: itemname,
          vendoremail: vendoremail,
          shopname: shopname,
          buyeremail: ls.get("email"),
          buyername: ls.get("username"),
          placedtime: time,
          quantity: q,
          addonname: selectedaddonname,
          addonprice: selectedaddonprice,
          totalprice: p,
        })
        .then((res) => {
          console.log(res);
        })
        .catch((err) => {
          console.log(err);
        });
      //deduct money
      axios
        .post("http://localhost:4000/buyer/deductmoney", {
          email: ls.get("email"),
          amount: p,
        })
        .then((res) => {
          console.log(res);
        })
        .catch((err) => {
          console.log(err);
        });
      onClose();
    }
  };

  const [refresh, setRefresh] = useState(false);

  useEffect(() => {
    axios
      .post("http://localhost:4000/buyer/getitem", {
        _id: selectedValue,
      })
      .then((res) => {
        setItemname(res.data.itemname);
        setShopname(res.data.shopname);
        setPrice(res.data.price);
        setAddonname(res.data.addonname);
        setAddonprice(res.data.addonprice);
        setVendoremail(res.data.manageremail);
      })
      .catch((err) => {
        console.log(err);
      });
    if (selectedValue === "" || selectedValue === undefined) {
      setRefresh(!refresh);
    }
  }, [refresh]);

  return (
    <Dialog onClose={handleClose} open={open}>
      <DialogTitle>A few more steps...</DialogTitle>
      <br></br>
      {/* Details of the product */}
      {/* {selectedValue} */}
      <Box>
        <Stack spacing={2}>
          <TextField
            id="outlined-basic"
            label="ItemName"
            variant="outlined"
            value={itemname}
            InputProps={{
              readOnly: true,
            }}
          />
          <TextField
            id="outlined-basic"
            label="ShopName"
            variant="outlined"
            value={shopname}
            InputProps={{
              readOnly: true,
            }}
          />
          <TextField
            id="outlined-basic"
            label="Price"
            variant="outlined"
            value={price}
            InputProps={{
              readOnly: true,
            }}
          />
          <TextField
            id="quantity"
            label="Quantity"
            type="number"
            variant="outlined"
            defaultValue={1}
            onChange={(e) => {
              setQuantity(e.target.value);
            }}
          />
          {/* Multi select AddOns */}
          <FormControl variant="outlined">
            <InputLabel id="demo-simple-select-outlined-label">
              AddOns
            </InputLabel>
            <Select
              labelId="demo-simple-select-outlined-label"
              id="demo-simple-select-outlined"
              multiple
              value={selectedaddonname}
              onChange={(e) => {
                setSelectedaddonname(e.target.value);
                // Set selectedaddonprice as the same index as selectedaddonname
                setSelectedaddonprice(
                  e.target.value.map((addon) => {
                    return addonprice[addonname.indexOf(addon)];
                  })
                );
              }}
              label="AddOns"
              variant="outlined"
            >
              {addonname.map((addon) => (
                <MenuItem key={addon} value={addon}>
                  {addon}
                </MenuItem>
              ))}
            </Select>
          </FormControl>
        </Stack>
      </Box>
      <br></br>
      <Button variant="contained" color="primary" onClick={handlebuy}>
        Buy
      </Button>
      {/* Set quantity to order */}
    </Dialog>
  );
}
