import * as React from 'react';
import List from '@mui/material/List';
import ListItem from '@mui/material/ListItem';  
import ListItemText from '@mui/material/ListItemText';
import Divider from '@mui/material/Divider';
import ls from "local-storage";
import Box from "@mui/material/Box";
import axios from 'axios';
import { useState } from 'react';
import { useEffect } from 'react';

const style = {
  width: '100%',
  maxWidth: 360,
  bgcolor: 'background.paper',
};

export default function MyProfile() {
     const [name, setName] = React.useState("");
     const [managername, setManagername] = React.useState("");
     const [shopname, setShopname] = React.useState("");
     const [email, setEmail] = React.useState("");
     const [contact, setContact] = React.useState("");
     const [age, setAge] = React.useState("");
     const [batch, setBatch] = React.useState("");
     const [opentime, setOpentime] = React.useState("");
     const [closetime, setClosetime] = React.useState("");

    //Use email from local storage top get necessary info from backend
    useEffect(() => {
        {
            ls.get("usertype") === 1 ?
            axios.get("http://localhost:4000/user/buyerdetails", {
                headers: {
                    "Content-Type": "application/json"
                },
                body: {
                    "email": ls.get("email")
                }
            })
            .then((response) => {
                setName(response.data.name);
                setEmail(response.data.email);
                setContact(response.data.contact);
                setAge(response.data.age);
                setBatch(response.data.batch);
            })
            .catch(function (error) {
                console.log(error);
            })
            :
            axios.get("http://localhost:4000/user/vendordetails", {
                params: {
                    email: ls.get("email")
                }
            })
            .then((response) => {
                setManagername(response.data.managername);
                setShopname(response.data.shopname);
                setEmail(response.data.email);
                setContact(response.data.contact);
                setOpentime(response.data.opentime);
                setClosetime(response.data.closetime);
            })
            .catch(function (error) {
                console.log(error);
            })
        }
    }, []);

  return (
    <List sx={style} component="nav" aria-label="mailbox folders">
    { ls.get("usertype") === 1 ?
    <Box>
      <ListItem button>
        <ListItemText primary="Inbox" />
      </ListItem>
      <Divider />
      <ListItem button divider>
        <ListItemText primary="Drafts" />
      </ListItem>
      <ListItem button>
        <ListItemText primary="Trash" />
      </ListItem>
      <Divider light />
      <ListItem button>
        <ListItemText primary="Spam" />
      </ListItem>
    </Box>
    :
    <Box>
      <ListItem button>
        <ListItemText primary="Name:" secondary={name} />
      </ListItem>
      <Divider />
      <ListItem button divider>
        <ListItemText primary="Drafts" />
      </ListItem>
      <ListItem button>
        <ListItemText primary="Trash" />
      </ListItem>
      <Divider light />
      <ListItem button>
        <ListItemText primary="Spam" />
      </ListItem>
    </Box>
    }
    </List>
  );
}
