import * as React from "react";
import Table from "@mui/material/Table";
import TableBody from "@mui/material/TableBody";
import TableCell from "@mui/material/TableCell";
import TableContainer from "@mui/material/TableContainer";
import TableHead from "@mui/material/TableHead";
import TableRow from "@mui/material/TableRow";
import Paper from "@mui/material/Paper";
import { useEffect, useState } from "react";
import ls from "local-storage";
import axios from "axios";
import { Button } from "@mui/material";
import Box from "@mui/material/Box";
import Stack from "@mui/material/Stack";
import InputLabel from "@mui/material/InputLabel";
import MenuItem from "@mui/material/MenuItem";
import FormControl from "@mui/material/FormControl";
import Select from "@mui/material/Select";
import { createTheme } from "@mui/material/styles";
import { deepOrange } from "@mui/material/colors";
import { yellow } from "@mui/material/colors";
import Chip from "@mui/material/Chip";
import OutlinedInput from "@mui/material/OutlinedInput";
import { TextField } from "@mui/material";
import PropTypes from "prop-types";
import DialogTitle from "@mui/material/DialogTitle";
import Dialog from "@mui/material/Dialog";

function SimpleDialog(props) {
  const { onClose, selectedValue, open } = props;
  const [quantity, setQuantity] = useState(1);
  const [itemname, setItemname] = useState("");
  const [shopname, setShopname] = useState("");
  const [price, setPrice] = useState(0);
  const [addonname, setAddonname] = useState([]);
  const [addonprice, setAddonprice] = useState([]);
  const [totalprice, setTotalprice] = useState(0);
  const handleClose = () => {
    onClose();
  };

  useEffect(() => {
    axios
      .post("http://localhost:4000/buyer/getitem", {
        _id: selectedValue,
      })
      .then((res) => {
        // setItemname(res.data.itemname);
        // setShopname(res.data.shopname);
        // setPrice(res.data.price);
        // setAddonname(res.data.addonname);
        // setAddonprice(res.data.addonprice);
      });
  }, []);

  return (
    <Dialog onClose={handleClose} open={open}>
      <DialogTitle>A few more steps...</DialogTitle>
      <br></br>
      {/* Details of the product */}
      {selectedValue}
      <Box>
        <Stack spacing={2}>
          <TextField
            id="outlined-basic"
            label="ItemName"
            variant="outlined"
            // value={itemname}
            InputProps={{
              readOnly: true,
            }}
          />
          <TextField
            id="outlined-basic"
            label="ShopName"
            variant="outlined"
            // value={shopname}
            InputProps={{
              readOnly: true,
            }}
          />
        </Stack>
        <TextField
          id="outlined-basic"
          label="Price"
          variant="outlined"
          // value={price}
          InputProps={{
            readOnly: true,
          }}
        />
        <TextField
          id="quantity"
          label="Quantity"
          type="number"
          variant="outlined"
          defaultValue={1}
          // onChange={(e) => {
          //   setQuantity(e.target.value);
          // }}
        />
      </Box>
      <br></br>
      <Button variant="contained" color="primary" onClick={handleClose}>
        Buy
      </Button>
      {/* Set quantity to order */}
    </Dialog>
  );
}

SimpleDialog.propTypes = {
  onClose: PropTypes.func.isRequired,
  open: PropTypes.bool.isRequired,
  selectedValue: PropTypes.string.isRequired,
};

export default function Myitem() {
  const [menu, setMenu] = useState([]);
  const [sortedmenu, setSortedMenu] = useState([]);
  const [refresh, setRefresh] = useState(false);

  const [open, setOpen] = useState(false);
  const [selectedValue, setSelectedValue] = useState("");

  const handleClickOpen = (props) => {
    // console.log(props);
    //set selected value as props
    setSelectedValue(props);
    console.log(selectedValue);
    setOpen(true);
  };

  const handleClose = (value) => {
    setOpen(false);
    setSelectedValue(value);
  };

  // Fill up the rows array with data.
  useEffect(() => {
    // Get all documents from the database for item for the particular vendor
    axios
      .post("http://localhost:4000/buyer/showmenu")
      .then((res) => {
        setMenu(res.data);
        setSortedMenu(res.data);
      })
      .catch((err) => {
        console.log(err);
      });
  }, [refresh]);

  const handlebuy = (key) => {
    //log the id, quantity, addons
    var quantity = document.getElementById(key).value;
    console.log(key);
    console.log(quantity);
    console.log(ls.get("email"));
  };

  return (
    //Favourites

    //Filters

    //Menu
    <Box>
      <TableContainer component={Paper}>
        <Table sx={{ minWidth: 650 }} aria-label="simple table">
          <TableHead>
            <TableRow>
              <TableCell>ItemName</TableCell>
              <TableCell align="center">Image</TableCell>
              <TableCell align="center">ShopName</TableCell>
              <TableCell align="center">Price</TableCell>
              <TableCell align="center">Veg/Non-Veg</TableCell>
              <TableCell align="center">Tags</TableCell>
              {/* Buying Stages */}
              <TableCell align="center">AddOns</TableCell>
              <TableCell align="center"></TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {menu.map((row) => (
              <TableRow
                key={row._id}
                sx={{ "&:last-child td, &:last-child th": { border: 0 } }}
              >
                <TableCell component="th" scope="row">
                  {row.itemname}
                </TableCell>
                <TableCell align="center">
                  <img
                    src={row.image}
                    alt="food"
                    style={{ width: "120px", height: "100px" }}
                  />
                </TableCell>
                <TableCell align="center">{row.shopname}</TableCell>
                <TableCell align="center">Rs.{row.price}</TableCell>
                <TableCell align="center">
                  {row.veg === true ? "Veg" : "Non-Veg"}
                </TableCell>
                <TableCell align="center">
                  {row.tags.map((tag) => (
                    <div spacing={2} key={tag}>
                      <Stack spacing={2}>
                        <Chip label={tag} key={tag} />
                      </Stack>
                    </div>
                  ))}
                </TableCell>
                <TableCell align="center">
                  {/* <FormControl fullWidth>
                  <InputLabel id="demo-simple-select-label">List</InputLabel>
                  <Select
                    labelId="demo-simple-select-label"
                    id="demo-simple-select"
                    // value={row.addonname}
                    label="List"
                  >
                    //map addonname and addonprice simultaneously
                    {row.addonname.map((addon, index) => (
                      <MenuItem key={index}>
                        {addon}: Rs.
                        {row.addonprice[index]}
                      </MenuItem>
                    ))}
                  </Select>
                </FormControl> */}
                  {row.addonname.map((addon, index) => (
                    <div spacing={2} key={index}>
                      <Stack spacing={2}>
                        {/* Addonname and addonprice */}
                        <Chip
                          label={`${addon}: Rs.${row.addonprice[index]}`}
                          key={index}
                        />
                      </Stack>
                    </div>
                  ))}
                </TableCell>
                <TableCell align="center">
                  <Button
                    variant="contained"
                    color="secondary"
                    onClick={() => handleClickOpen(row._id)}
                  >
                    Buy
                  </Button>
                </TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>
      <SimpleDialog
        selectedValue={selectedValue}
        open={open}
        onClose={handleClose}
      />
    </Box>
  );
}
