import * as React from 'react';
import Table from '@mui/material/Table';
import TableBody from '@mui/material/TableBody';
import TableCell from '@mui/material/TableCell';
import TableContainer from '@mui/material/TableContainer';
import TableHead from '@mui/material/TableHead';
import TableRow from '@mui/material/TableRow';
import Paper from '@mui/material/Paper';
import { useEffect, useState } from 'react';
import ls from 'local-storage';
import axios from 'axios';


function createData(ItemName, PlacedTime, Quantity, BuyerName, AddOns, Status) {
  return { ItemName, PlacedTime, Quantity, BuyerName, AddOns, Status };
}

const initialrows = [
  createData('Frozen yoghurt', 159, 6.0, 'Me', 24, 4.0),

];

export default function BasicTable() {
    const [rows, setRows] = useState(initialrows);

    // Fill up the rows array with data.
    useEffect(() => {
        // Get all documents from the database for orders for the particular vendor
        axios.post('http://localhost:4000/vendor/showorders', {
            manangeremail: ls.get('email'),
    })
        .then(function (response) {
            console.log(response);
            setRows(response.data);
        })
        .catch(function (error) {
            console.log(error);
        });
    })


  return (
    <TableContainer component={Paper}>
      <Table sx={{ minWidth: 650 }} aria-label="simple table">
        <TableHead>
          <TableRow>
            <TableCell>ItemName</TableCell>
            <TableCell align="right">PlacedTime</TableCell>
            <TableCell align="right">Quantity</TableCell>
            <TableCell align="right">BuyerName</TableCell>
            <TableCell align="right">AddOns</TableCell>
            <TableCell align="right">Status</TableCell>
            <TableCell align="right">Action</TableCell>
          </TableRow>
        </TableHead>
        <TableBody>
          {rows.map((row) => (
            <TableRow
              key={row.name}
              sx={{ '&:last-child td, &:last-child th': { border: 0 } }}
            >
              <TableCell component="th" scope="row">
                {row.ItemName}
              </TableCell>
              <TableCell align="right">{row.PlacedTime}</TableCell>
              <TableCell align="right">{row.Quantity}</TableCell>
              <TableCell align="right">{row.BuyerName}</TableCell>
              <TableCell align="right">{row.AddOns}</TableCell>
              <TableCell align="right">{row.Status}</TableCell>

            </TableRow>
          ))}
        </TableBody>
      </Table>
    </TableContainer>
  );
}
