import * as React from 'react';
import List from '@mui/material/List';
import ListItem from '@mui/material/ListItem';  
import ListItemText from '@mui/material/ListItemText';
import Divider from '@mui/material/Divider';
import ls from "local-storage";
import Box from "@mui/material/Box";
import axios from 'axios';
import { useState } from 'react';
import { useEffect } from 'react';
import { Button, TextField } from '@mui/material';
import Stack from '@mui/material/Stack';

const style = {
  width: '100%',
  maxWidth: 360,
  bgcolor: 'background.paper',
};

export default function MyProfile() {
     const [name, setName] = React.useState("");
     const [managername, setManagername] = React.useState("");
     const [shopname, setShopname] = React.useState("");
     const [password, setPassword] = React.useState("");
     const [contact, setContact] = React.useState("");
     const [age, setAge] = React.useState("");
     const [batch, setBatch] = React.useState("");
     const [opentime, setOpentime] = React.useState("");
     const [closetime, setClosetime] = React.useState("");

     const [edit, setEdit] = React.useState(false);

     const handleedit = () => {
          setEdit(true);
          //make the fields editable and change the defaultValue


      };

      const handlesave = () => {
          setEdit(false);
          if(ls.get("usertype") === 1) {
            //Set values from the text fields
            setName(document.getElementById("name").value);
            setContact(document.getElementById("contact").value);


            axios.post('http://localhost:4000/user/buyerupdate', {
                email: ls.get("email"),
                name: name,
                contact: contact,
                age: age,
                batch: batch,
          })
          .then(function (response) {
              console.log(response);
          })
          .catch(function (error) {
              console.log(error);
          });
          }
          else if(ls.get("usertype") === 2) {
            axios.post('http://localhost:4000/user/vendorupdate', {
                email: ls.get("email"),
                managername: managername,
                shopname: shopname,
                contact: contact,
                opentime: opentime,
                closetime: closetime,
          })
          .then(function (response) {
              console.log(response);
          })
          .catch(function (error) {
              console.log(error);
          });
          }
      };


    //Use email from local storage top get necessary info from backend
    useEffect(() => {
        {
            ls.get("usertype") === 1 ?
            axios.post("http://localhost:4000/user/buyerdetails", {
                email: ls.get("email")
            })
            .then((response) => {
                setName(response.data.name);
                setContact(response.data.contact);
                setAge(response.data.age);
                setBatch(response.data.batch);
            })
            .catch(function (error) {
                console.log(error);
            })
            :
            axios.post("http://localhost:4000/user/vendordetails", {
                email: ls.get("email")
            })
            .then((response) => {
                setManagername(response.data.managername);
                setShopname(response.data.shopname);
                setContact(response.data.contact);
                setOpentime(response.data.opentime);
                setClosetime(response.data.closetime);
            })
            .catch(function (error) {
                console.log(error);
            })
        }
    }, []);

  return (
    <List sx={style} component="nav" aria-label="mailbox folders">
    { ls.get("usertype") === 1 ?
    <Box>
      <ListItem button>
        <ListItemText primary="Name:" />
        <TextField 
            id='name'
            name='name'
            variant="outlined"
            defaultValue={name}
            InputProps={{
                readOnly: !edit,
            }}
            key={`${Math.floor((Math.random() * 1000))}-min`}
        />
      </ListItem>
      <Divider />
      {/* <ListItem button>
        <ListItemText primary="Password:" />
        <TextField
            id="outlined-basic"
            variant="outlined"
            defaultValue={password}
            InputProps={{
              readOnly: !edit,
            }}
            key={`${Math.floor((Math.random() * 1000))}-min`}
        />
      </ListItem>
      <Divider /> */}
      <ListItem button>
        <ListItemText primary="Contact:" />
        <TextField
            id='contact'
            name='contact'
            variant="outlined"
            defaultValue={contact}
            InputProps={{
              readOnly: !edit,
            }}
            key={`${Math.floor((Math.random() * 1000))}-min`}
        />
      </ListItem>
      <Divider />
      <ListItem button>
        <ListItemText primary="Age" />
        <TextField
            id='age'
            name='age'
            variant="outlined"
            defaultValue={age}
            InputProps={{
              readOnly: !edit,
            }}
            key={`${Math.floor((Math.random() * 1000))}-min`}
        />
      </ListItem>
      <Divider />
      <ListItem button>
        <ListItemText primary="Batch" />
        <TextField
            id='batch'
            name='batch'
            variant="outlined"
            defaultValue={ batch }
            InputProps={{
              readOnly: !edit,
            }}
            key={`${Math.floor((Math.random() * 1000))}-min`}
        />
      </ListItem>
      <Divider light />
      <br/>
      <br/>
      <br/>
      <Stack
        spacing={5}
        direction="row"
        >
      <Button
        variant="contained"
        color="primary"
        disabled = {edit}
        onClick={handleedit}
        >
        Edit Profile
        </Button>
        <Button
        variant="contained"
        color="primary"
        disabled = {!edit}
        onClick={handlesave}
        >
          Save Profile
        </Button>
      </Stack>
    </Box>
    :
    <Box>
      <ListItem button>
        <ListItemText primary="Manager Name:"/>
        <TextField 
            id='managername'
            name='managername'
            variant="outlined"
            defaultValue={managername}
            InputProps={{
                readOnly: !edit,
            }}
            key={`${Math.floor((Math.random() * 1000))}-min`}
        />
      </ListItem>
      <Divider />
      <ListItem button>
        <ListItemText primary="Shop Name:" />
        <TextField
            id='shopname'
            name='shopname'
            variant="outlined"
            defaultValue={shopname}
            InputProps={{
                readOnly: !edit,
            }}
            key={`${Math.floor((Math.random() * 1000))}-min`}
        />
      </ListItem>
      <Divider />
      {/* <ListItem button>
        <ListItemText primary="Password:" />
        <TextField
            id="outlined-basic"
            variant="outlined"
            defaultValue={password}
            InputProps={{
                readOnly: !edit,
            }}
            key={`${Math.floor((Math.random() * 1000))}-min`}
        />
      </ListItem>
      <Divider /> */}
      <ListItem button>
        <ListItemText primary="Contact:" />
        <TextField
            id='contactv'
            name='contactv'
            variant="outlined"
            defaultValue={contact}
            InputProps={{
                readOnly: !edit,
            }}
            key={`${Math.floor((Math.random() * 1000))}-min`}
        />
      </ListItem>
      <Divider />
      <ListItem button>
        <ListItemText primary="Open Time:" />
        <TextField
            id='opentime'
            name='opentime'
            variant="outlined"
            defaultValue={opentime}
            InputProps={{
                readOnly: !edit,
            }}
            key={`${Math.floor((Math.random() * 1000))}-min`}
        />
      </ListItem>
      <Divider />
      <ListItem button>
        <ListItemText primary="Close Time:" />
        <TextField
            id='closetime'
            name='closetime'
            variant="outlined"
            defaultValue={closetime}
            InputProps={{
                readOnly: !edit,
            }}
            key={`${Math.floor((Math.random() * 1000))}-min`}
        />
      </ListItem>
      <Divider light />
      <br/>
      <br/>
      <br/>
      <Stack
        spacing={5}
        direction="row"
        >
      <Button
        variant="contained"
        color="primary"
        disabled = {edit}
        onClick={handleedit}
        >
        Edit Profile
        </Button>
        <Button
        variant="contained"
        color="primary"
        disabled = {!edit}
        onClick={handlesave}
        >
          Save Profile
        </Button>
      </Stack>
    </Box>
    }
    </List>
  );
}
