import * as React from 'react';
import List from '@mui/material/List';
import ListItem from '@mui/material/ListItem';  
import ListItemText from '@mui/material/ListItemText';
import Divider from '@mui/material/Divider';
import ls from "local-storage";
import Box from "@mui/material/Box";
import axios from 'axios';
import { useState } from 'react';
import { useEffect } from 'react';
import { Button, ListSubheader, TextField } from '@mui/material';


export default function Menu() {

        const [food, setFood] = useState([]);

        useEffect(() => {
            axios.get('http://localhost:4000/user/menu')
            .then(function (response) {
                console.log(response);
                setFood(response.data);
            })
            .catch(function (error) {
                console.log(error);
            });
        }
        , []);

        return (
            <div>
                <Box sx={{ flexGrow: 1 }}>
                    <List>
                        <ListSubheader><center><h3>Food Menu</h3></center></ListSubheader>
                        <Divider />
                        {food.map(item => (
                            <ListItem>
                                <ListItemText primary={item.name} secondary={item.price} />
                            </ListItem>
                        ))}
                        <Divider />
                    </List>
                    </Box>
                <Box p={2}>
                    <Stack>
                        <List>
                            <ListItem>
                                <ListItemText primary="Orders Placed:" secondary="0" />
                            </ListItem>
                            <Divider />
                            <ListItem>
                                <ListItemText primary="Orders Pending:" secondary="0" />
                            </ListItem>
                            <Divider />
                            <ListItem>
                                <ListItemText primary="Orders Completed:" secondary="0" />
                            </ListItem>
                            <Divider />
                        </List>
                    </Stack>
                </Box>
            </div>
        );
    }
