import * as React from 'react';
import Table from '@mui/material/Table';
import TableBody from '@mui/material/TableBody';
import TableCell from '@mui/material/TableCell';
import TableContainer from '@mui/material/TableContainer';
import TableHead from '@mui/material/TableHead';
import TableRow from '@mui/material/TableRow';
import Paper from '@mui/material/Paper';
import { useEffect, useState } from 'react';
import ls from 'local-storage';
import axios from 'axios';
import { Button } from '@mui/material';
import Box from "@mui/material/Box";




export default function MyOrders() {
    const [orders, setOrders] = useState([]);
    const [sortedorders, setSortedOrders] = useState([]);

    // Fill up the rows array with data.
    useEffect(() => {
        // Get all documents from the database for orders for the particular vendor
        axios.post('http://localhost:4000/buyer/showorders', {
            manageremail: ls.get('email')
        })
        .then((response) => {
          setOrders(response.data);
          setSortedOrders(response.data);
          console.log(response.data);
        })
        .catch((error) => {
          console.log(error);
        });
    }, []);

    const handlenextstage = (props) => {
        axios.post('http://localhost:4000/vendor/updateorderstatus', {
            _id: props._id,
            aorr: "a",
        })
        .then(function (response) {
            console.log(response);
        })
        .catch(function (error) {
            console.log(error);
        });
    }

    const handlecancel = (props) => {
        axios.post('http://localhost:4000/vendor/updateorderstatus', {
            _id: props._id,
            aorr: "r",
        })
        .then(function (response) {
            console.log(response);
        })
        .catch(function (error) {
            console.log(error);
        });
    }

  return (

    <TableContainer component={Paper}>
      <Table sx={{ minWidth: 650 }} aria-label="simple table">
        <TableHead>
          <TableRow>
            <TableCell>ItemName</TableCell>
            <TableCell align="right">PlacedTime</TableCell>
            <TableCell align="right">Quantity</TableCell>
            <TableCell align="right">BuyerName</TableCell>
            <TableCell align="right">AddOns</TableCell>
            <TableCell align="right">Total</TableCell>
            <TableCell align="right">Status</TableCell>
            <TableCell align="right">Action</TableCell>
            <TableCell align="right">Cancel</TableCell>
          </TableRow>
        </TableHead>
        <TableBody>
          {orders.map((orders) => (
            <TableRow
              key={orders._id}
              sx={{ '&:last-child td, &:last-child th': { border: 0 } }}
            >
              <TableCell component="th" scope="row">
                {orders.itemname}
              </TableCell>
              {/* Convert number to unix time */}
              <TableCell align="right">{new Date(orders.placedtime).toLocaleString()}</TableCell>
              <TableCell align="right">{orders.quantity}</TableCell>
              <TableCell align="right">{orders.buyername}</TableCell>
              <TableCell align="right">{orders.addonname}</TableCell>
              <TableCell align="right">Rs.{orders.totalprice}</TableCell>
              <TableCell align="right">{orders.status}</TableCell>

                    { orders.status === "REJECTED" ?
                    <TableCell align="right">
                        <Button variant="contained" color="primary" disabled>
                            Next Stage
                        </Button>
                    </TableCell>
                    : 
                    <TableCell align="right">
                    <Button
                        variant="contained"
                        color="primary"
                        onClick={() => handlenextstage(orders)}
                    >Next stage</Button> 
                </TableCell>
                    }
                {
                    orders.status === 'PLACED' ?
                    <TableCell align="right">
                        <Button variant="contained" onClick={() => handlecancel(orders)}>Cancel</Button>
                    </TableCell>
                    : 
                    <TableCell align="right">
                        <Button variant="contained" disabled>Cancel</Button>
                    </TableCell>
                }
            </TableRow>
          ))}
        </TableBody>
      </Table>
    </TableContainer>
  );
}
