import * as React from 'react';
import Table from '@mui/material/Table';
import TableBody from '@mui/material/TableBody';
import TableCell from '@mui/material/TableCell';
import TableContainer from '@mui/material/TableContainer';
import TableHead from '@mui/material/TableHead';
import TableRow from '@mui/material/TableRow';
import Paper from '@mui/material/Paper';
import { useEffect, useState } from 'react';
import ls from 'local-storage';
import axios from 'axios';
import { Button } from '@mui/material';
import Box from "@mui/material/Box";


function createData(_id, ItemName, PlacedTime, Quantity, BuyerName, AddOns, Status) {
  return {_id, ItemName, PlacedTime, Quantity, BuyerName, AddOns, Status };
}

const initialrows = [
  createData('dgg', 'Frozen yoghurt', 159, 6.0, 'Me', 24, 'ACCEPTED'),
  createData('dfdg', 'Frozen yoghurt', 159, 6.0, 'Me', 24, 'PLACED'),

];

export default function BasicTable() {
    const [rows, setRows] = useState(initialrows);
    const [orders, setOrders] = useState([]);
    const [sortedorders, setSortedOrders] = useState([]);

    // Fill up the rows array with data.
    useEffect(() => {
        // Get all documents from the database for orders for the particular vendor
        axios.post('http://localhost:4000/vendor/showorders', {
            manageremail: ls.get('email')
        })
        .then((response) => {
          setOrders(response.data);
          setSortedOrders(response.data);
          console.log(response.data);
        })
        .catch((error) => {
          console.log(error);
        });
    }, []);

    const handlenextstage = (props) => {
        axios.post('http://localhost:4000/vendor/updateorderstatus', {
            _id: props._id,
            aorr: "a",
        })
        .then(function (response) {
            console.log(response);
        })
        .catch(function (error) {
            console.log(error);
        });
    }


  return (

    <TableContainer component={Paper}>
      <Table sx={{ minWidth: 650 }} aria-label="simple table">
        <TableHead>
          <TableRow>
            <TableCell>ItemName</TableCell>
            <TableCell align="right">PlacedTime</TableCell>
            <TableCell align="right">Quantity</TableCell>
            <TableCell align="right">BuyerName</TableCell>
            <TableCell align="right">AddOns</TableCell>
            <TableCell align="right">Status</TableCell>
            <TableCell align="right">Action</TableCell>
            <TableCell align="right">Cancel</TableCell>
          </TableRow>
        </TableHead>
        <TableBody>
          {rows.map((row) => (
            <TableRow
              key={row._id}
              sx={{ '&:last-child td, &:last-child th': { border: 0 } }}
            >
              <TableCell component="th" scope="row">
                {row.itemname}
              </TableCell>
              <TableCell align="right">{row.placedtime}</TableCell>
              <TableCell align="right">{row.quantity}</TableCell>
              <TableCell align="right">{row.buyername}</TableCell>
              <TableCell align="right">{row.addonname}: {row.addonprice}</TableCell>
              <TableCell align="right">{row.status}</TableCell>
                <TableCell align="right">
                    <Button
                        variant="contained"
                        color="primary"
                        onClick={() => handlenextstage(row)}
                    >Next stage</Button>
                </TableCell>
                {
                    row.status === 'PLACED' ?
                    <TableCell align="right">
                        <Button variant="contained">Cancel</Button>
                    </TableCell>
                    :
                    <TableCell align="right">
                        <Button variant="contained" disabled>Cancel</Button>
                    </TableCell>
                }
            </TableRow>
          ))}
        </TableBody>
      </Table>
    </TableContainer>
  );
}
