import * as React from 'react';
import List from '@mui/material/List';
import ListItem from '@mui/material/ListItem';  
import ListItemText from '@mui/material/ListItemText';
import Divider from '@mui/material/Divider';
import ls from "local-storage";
import Box from "@mui/material/Box";
import axios from 'axios';
import { useState } from 'react';
import { useEffect } from 'react';
import { Button, TextField } from '@mui/material';
import Stack from '@mui/material/Stack';
import { width } from '@mui/system';

const style = {
  width: '100%',
  maxWidth: 360,
  bgcolor: 'background.paper',
};

export default function Editing() {
     const [itemname, setItemname] = React.useState("");
     const [price, setPrice] = React.useState(0);
     const [veg, setVeg] = React.useState(true);
     const [tags, setTags] = React.useState([]);
     const [addonname, setAddonname] = React.useState([]);
     const [addonprice, setAddonprice] = React.useState([]);

     const [edit, setEdit] = React.useState(true);

     const handleedit = () => {
          setEdit(true);
          //make the fields editable and change the defaultValue
      };

      const handlesave = () => {
          setEdit(false);
        //   if(ls.get("usertype") === 1) {
        //     //Set values from the text fields
        //     setitemname(document.getElementById("itemname").value);
        //     setContact(document.getElementById("contact").value);
        //     setAge(document.getElementById("age").value);
        //     setBatch(document.getElementById("batch").value);

        //     axios.post('http://localhost:4000/user/buyerupdate', {
        //         email: ls.get("email"),
        //         itemname: document.getElementById("itemname").value,
        //         contact: document.getElementById("contact").value,
        //         age: document.getElementById("age").value,
        //         batch: document.getElementById("batch").value
        //   })
        //   .then(function (response) {
        //       console.log(response);
        //   })
        //   .catch(function (error) {
        //       console.log(error);
        //   });
        //   alert("Profile Updated");
        //   window.location.reload();
        //   }
        //   else if(ls.get("usertype") === 2) {
        //     //Set values from the text fields
        //     setManageritemname(document.getElementById("manageritemname").value);
        //     console.log(manageritemname);
        //     setShopitemname(document.getElementById("shopitemname").value);
        //     setContact(document.getElementById("contactv").value);
        //     setOpentime(document.getElementById("opentime").value);
        //     setClosetime(document.getElementById("closetime").value);

        //     axios.post('http://localhost:4000/user/vendorupdate', {
        //         email: ls.get("email"),
        //         manageritemname: document.getElementById("manageritemname").value,
        //         shopitemname: document.getElementById("shopitemname").value,
        //         contact: document.getElementById("contactv").value,
        //         opentime: document.getElementById("opentime").value,
        //         closetime: document.getElementById("closetime").value,
        //   })
        //   .then(function (response) {
        //       console.log(response);
        //   })
        //   .catch(function (error) {
        //       console.log(error);
        //   });
        //   alert("Profile Updated");
        //   window.location.reload();
        //   }
      };

      //Use edit id from local storage
    useEffect(() => {
        if(ls.get("editid") === undefined) {
        }
        else {
            axios.post('http://localhost:4000/vendor/fooditem', {
                _id: ls.get("editid")
            })
            .then(function (response) {
                console.log(response);
                setItemname(response.data.itemname);
                setPrice(response.data.price);
                setVeg(response.data.veg);
                setTags(response.data.tags);
                setAddonname(response.data.addonname);
                setAddonprice(response.data.addonprice);
            })
            .catch(function (error) {
                console.log(error);
            });
        }
    }, []);


  return (
    <List sx={style} component="nav" aria-label="mailbox folders">
        <Stack
        spacing={5}
        direction="row"
        >
      <Button
        variant="contained"
        color="primary"
        >
        Back
        </Button>
      </Stack>
      <br/>
      <br/>
    <Box>
      <ListItem button>
        <ListItemText primary="Item Name:"/>
        <TextField 
            id='itemname'
            itemname='itemname'
            variant="outlined"
            defaultValue={itemname}
            key={`${Math.floor((Math.random() * 1000))}-min`}
        />
      </ListItem>
      <Divider />
      <ListItem button>
        <ListItemText primary="Price:" />
        <TextField
            id='price'
            itemname='price'
            variant="outlined"
            defaultValue={price}
            key={`${Math.floor((Math.random() * 1000))}-min`}
        />
      </ListItem>
      <Divider />
      <ListItem button>
        <ListItemText primary="Veg/Non Veg:" />
        <TextField
            id='veg'
            itemname='veg'
            variant="outlined"
            defaultValue={veg}
            key={`${Math.floor((Math.random() * 1000))}-min`}
        />
      </ListItem>
      <Divider />
      <ListItem button>
        <ListItemText primary="Tags:" />
        <TextField
            id='tags'
            itemname='tags'
            variant="outlined"
            defaultValue={tags}
            key={`${Math.floor((Math.random() * 1000))}-min`}
        />
      </ListItem>
      <Divider />
      <ListItem button>
        <ListItemText primary="Addon Name:" />
        <TextField
            id='addonname'
            itemname='addonname'
            variant="outlined"
            defaultValue={addonname}
            key={`${Math.floor((Math.random() * 1000))}-min`}
        />
      </ListItem>
      <Divider />
      <ListItem button>
        <ListItemText primary="Addon Price:" />
        <TextField
            id='addonprice'
            itemname='addonprice'
            variant="outlined"
            defaultValue={addonprice}
            key={`${Math.floor((Math.random() * 1000))}-min`}
        />
      </ListItem>
      <Divider light />
      <br/>
      <br/>
      <Stack
        spacing={5}
        direction="row"
        >
        <Button
        variant="contained"
        color="primary"
        disabled = {!edit}
        onClick={handlesave}
        >
          Save
        </Button>
      </Stack>
    </Box>
    </List>
  );
}
