  const [shopname, setShopname] = useState("");
  const [price, setPrice] = useState(0);
  const [addonname, setAddonname] = useState([]);
  const [addonprice, setAddonprice] = useState([]);
  const [totalprice, setTotalprice] = useState(0);
  const handleClose = () => {
    onClose();
  };
  const [refresh, setRefresh] = useState(false);

  useEffect(() => {
    axios
      .post("http://localhost:4000/buyer/getitem", {
        _id: selectedValue,
      })
      .then((res) => {
        setItemname(res.data.itemname);
        setShopname(res.data.shopname);
        setPrice(res.data.price);
        setAddonname(res.data.addonname);
        setAddonprice(res.data.addonprice);
      })
      .catch((err) => {
        console.log(err);
      });
    setRefresh(true);
  }, [refresh]);

  return (
    <Dialog onClose={handleClose} open={open}>
      <DialogTitle>A few more steps...</DialogTitle>
      <br></br>
      {/* Details of the product */}
      {selectedValue}
      <Box>
        <Stack spacing={2}>
          <TextField
            id="outlined-basic"
            label="ItemName"
            variant="outlined"
            value={itemname}
            InputProps={{
              readOnly: true,
            }}
          />
          <TextField
            id="outlined-basic"
            label="ShopName"
            variant="outlined"
            value={shopname}
            InputProps={{
              readOnly: true,
            }}
          />
        </Stack>
        <TextField
          id="outlined-basic"
          label="Price"
          variant="outlined"
          value={price}
          InputProps={{
            readOnly: true,
          }}
        />
        <TextField
          id="quantity"
          label="Quantity"
          type="number"
          variant="outlined"
          defaultValue={1}
          onChange={(e) => {
            setQuantity(e.target.value);
          }}
        />
      </Box>
      <br></br>
      <Button variant="contained" color="primary" onClick={handleClose}>
        Buy
      </Button>
      {/* Set quantity to order */}
    </Dialog>
  );
}
