import * as React from "react";
import List from "@mui/material/List";
import ListItem from "@mui/material/ListItem";
import ListItemText from "@mui/material/ListItemText";
import Divider from "@mui/material/Divider";
import ls from "local-storage";
import Box from "@mui/material/Box";
import axios from "axios";
import { useState } from "react";
import { useEffect } from "react";
import { Button, ListSubheader, TextField } from "@mui/material";
import Stack from "@mui/material/Stack";
import {
  ArgumentAxis,
  ValueAxis,
  Chart,
  BarSeries,
} from "@devexpress/dx-react-chart-material-ui";
import { RemoveFromQueue } from "@mui/icons-material";

var databatch = [];
var dataage = [];

var freqbatch = [0,0,0,0,0,0,0];
var freqage = [];
function Creategraph() {
    var data1 = [{argument: 0, value: 0}, {argument: 1, value: 0}, {argument: 2, value: 0}, {argument: 3, value: 0}, {argument: 4, value: 0}, {argument: 5, value: 0}, {argument: 6, value: 0}];
    var data2 = [];
  // useEffect(() => {
  //   // upload the count of a particular batch in data1
  //   for(let i = 0; i < freqbatch.length; i++){
  //     data1[freqbatch[i].batch].value = freqbatch[i].count;
  //   }

  // }, []);

  return (
    // <Chart style={{ height: "100px", width: "500px" }} data={data1}>
    //   <ArgumentAxis />
    //   <ValueAxis />
    //   <BarSeries valueField="value" argumentField="argument" />
    // </Chart>
    <Box>Hi</Box>
  );
}
//argument and value fields
//for creating the graph

export default function VStatistics() {
  const [completedemails, setCompletedEmails] = useState([]);
  const [allbuyers, setAllBuyers] = useState([]);
  const [refresh, setRefresh] = useState(false);
  const [placedorders, setPlacedOrders] = useState([]);
  const [pendingorders, setPendingOrders] = useState([]);
  const [completeorders, setCompleteOrders] = useState([]);
  const [array_5, setArray_5] = useState(["", "", "", "", ""]);
  useEffect(() => {
    axios
      .post("http://localhost:4000/vendor/allemails", {
        email: ls.get("email"),
      })
      .then((response) => {
        setCompletedEmails(response.data.emails);
      })
      .catch((error) => {
        console.log(error);
      });
    axios
      .post("http://localhost:4000/vendor/allbuyers")
      .then((response) => {
        setAllBuyers(response.data);
      })
      .catch((error) => {
        console.log(error);
      });
    axios
      .post("http://localhost:4000/vendor/statsplaced", {
        email: ls.get("email"),
      })
      .then((res) => {
        setPlacedOrders(res.data.placed);
      })
      .catch((err) => {
        console.log(err);
      });
    axios
      .post("http://localhost:4000/vendor/statspending", {
        email: ls.get("email"),
      })
      .then((res) => {
        setPendingOrders(res.data.pending);
      })
      .catch((err) => {
        console.log(err);
      });
    axios
      .post("http://localhost:4000/vendor/statscompleted", {
        email: ls.get("email"),
      })
      .then((res) => {
        setCompleteOrders(res.data.completed);
      })
      .catch((err) => {
        console.log(err);
      });
    axios
      .post("http://localhost:4000/vendor/statstop", {
        email: ls.get("email"),
      })
      .then((res) => {
        setArray_5(res.data.item);
      })
      .catch((err) => {
        console.log(err);
      });

    //compare emails with allbuyers
    for (let i = 0; i < completedemails.length; i++) {
      for (let j = 0; j < allbuyers.emails.length; j++) {
        if (completedemails[i] === allbuyers.emails[j]) {
          databatch.push({
            argument: allbuyers.batch[j],
            value: 1,
          }
          );
          dataage.push({
            argument: allbuyers.age[j],
            value: 1,
          }
          );
          freqbatch[allbuyers.batch[j]]++;
          freqage.push(allbuyers.age[j]);
        }
      }
    }
  }, [refresh]);

  return (
    <div>
      <Box sx={{ flexGrow: 1 }}>
        <List>
          <ListSubheader>
            <center>
              <h3>Most ordered food items</h3>
            </center>
          </ListSubheader>
          <Divider />
          <ListItem>
            <ListItemText primary="Item 1" secondary={array_5[0]} />
            <ListItemText primary="Item 2" secondary={array_5[1]} />
            <ListItemText primary="Item 3" secondary={array_5[2]} />
            <ListItemText primary="Item 4" secondary={array_5[3]} />
            <ListItemText primary="Item 5" secondary={array_5[4]} />
          </ListItem>
          <Divider />
        </List>
      </Box>
      <Box p={2}>
        <Stack>
          <List>
            <ListItem>
              <ListItemText primary="Orders Placed:" secondary={placedorders} />
            </ListItem>
            <Divider />
            <ListItem>
              <ListItemText
                primary="Orders Pending:"
                secondary={pendingorders}
              />
            </ListItem>
            <Divider />
            <ListItem>
              <ListItemText
                primary="Orders Completed:"
                secondary={completeorders}
              />
            </ListItem>
            <Divider />
          </List>
        </Stack>
      </Box>
      <Creategraph />
    </div>
  );
}
