import * as React from 'react';
import Table from '@mui/material/Table';
import TableBody from '@mui/material/TableBody';
import TableCell from '@mui/material/TableCell';
import TableContainer from '@mui/material/TableContainer';
import TableHead from '@mui/material/TableHead';
import TableRow from '@mui/material/TableRow';
import Paper from '@mui/material/Paper';
import { useEffect, useState } from 'react';
import ls from 'local-storage';
import axios from 'axios';
import { Button } from '@mui/material';
import Box from "@mui/material/Box";
import Stack from '@mui/material/Stack';


function createData(ItemName, Price, VNV, Tags, AddOns) {
  return {ItemName, Price, VNV, Tags, AddOns};
}

const initialrows = [
  createData('Frozen yoghurt', 159, 6.0, 'Me', 24, 'ACCEPTED'),
  createData('Frozen yoghurt', 159, 6.0, 'Me', 24, 'PLACED'),

];

export default function ShowMenu() {
    const [rows, setRows] = useState(initialrows);

    // Fill up the rows array with data.
    useEffect(() => {
        // Get all documents from the database for orders for the particular vendor
        axios.post('http://localhost:4000/vendor/showmenu', {
            manangeremail: ls.get('email'),
    })
        .then(function (response) {
            console.log(response);
            setRows(response.data);
        })
        .catch(function (error) {
            console.log(error);
        });
    })


  return (
    <Box>
    <Stack
              sx={{ pt: 4 }}
              direction="row"
              spacing={2}
              justifyContent="center"
            >
    <Button variant="contained">Add Item</Button>
    </Stack>

    <TableContainer component={Paper}>
      <Table sx={{ minWidth: 650 }} aria-label="simple table">
        <TableHead>
          <TableRow>
            <TableCell>ItemName</TableCell>
            <TableCell align="right">Price</TableCell>
            <TableCell align="right">Veg/Non-Veg</TableCell>
            <TableCell align="right">Tags</TableCell>
            <TableCell align="right">AddOns</TableCell>
            <TableCell align="right">Edit</TableCell>
            <TableCell align="right">Delete</TableCell>
          </TableRow>
        </TableHead>
        <TableBody>
          {rows.map((row) => (
            <TableRow
              key={row.name}
              sx={{ '&:last-child td, &:last-child th': { border: 0 } }}
            >
              <TableCell component="th" scope="row">
                {row.ItemName}
              </TableCell>
              <TableCell align="right">{row.PlacedTime}</TableCell>
              <TableCell align="right">{row.Quantity}</TableCell>
              <TableCell align="right">{row.BuyerName}</TableCell>
              <TableCell align="right">{row.AddOns}</TableCell>
              <TableCell align="right">{row.Status}</TableCell>
                <TableCell align="right">
                    <Button variant="contained">Edit</Button>
                </TableCell>
                {
                    row.Status === 'PLACED' ?
                    <TableCell align="right">
                        <Button variant="contained">Cancel</Button>
                    </TableCell>
                    :
                    <TableCell align="right">
                        <Button variant="contained" disabled>Cancel</Button>
                    </TableCell>
                }
            </TableRow>
          ))}
        </TableBody>
      </Table>
    </TableContainer>
    </Box>
  );
}
