import * as React from "react";
import Avatar from "@mui/material/Avatar";
import Button from "@mui/material/Button";
import CssBaseline from "@mui/material/CssBaseline";
import TextField from "@mui/material/TextField";
import Grid from "@mui/material/Grid";
import Box from "@mui/material/Box";
import LockOutlinedIcon from "@mui/icons-material/LockOutlined";
import Typography from "@mui/material/Typography";
import Container from "@mui/material/Container";
import { createTheme, ThemeProvider } from "@mui/material/styles";
import { MenuItem } from "@mui/material";
import Select from "@mui/material/Select";
import InputLabel from "@mui/material/InputLabel";
import { FormControl } from "@mui/material";
import axios from "axios";

// function Copyright(props) {
//   return (
//     <Typography variant="body2" color="text.secondary" align="center" {...props}>
//       {'Copyright Â© '}
//       <Link color="inherit" href="https://mui.com/">
//         Your Website
//       </Link>{' '}
//       {new Date().getFullYear()}
//       {'.'}
//     </Typography>
//   );
// }

const theme = createTheme();

export default function Adding() {
    const [itemname, setItemname] = React.useState("");
    const [price, setPrice] = React.useState(0);
    const [veg, setVeg] = React.useState(true);
    const [tags, setTags] = React.useState([]);
    const [addonname, setAddonsname] = React.useState([]);
    const [addonprice, setAddonsprice] = React.useState([]);

  const handleitemname = (event) => {
    setItemname(event.target.value);
    };
    const handleprice = (event) => {
    setPrice(event.target.value);
    };
    const handleveg = (event) => {
    setVeg(event.target.value);
    };
    const handletags = (event) => {
    setTags(event.target.value);
    };
  const handleRegistration = (event) => {
  };

  return (
    <ThemeProvider theme={theme}>
      <Container component="main" maxWidth="xs">
        <CssBaseline />
        <Box
          sx={{
            marginTop: 8,
            display: "flex",
            flexDirection: "column",
            alignItems: "center",
          }}
        >
          {/* <Avatar sx={{ m: 1, bgcolor: "secondary.main" }}>
            <LockOutlinedIcon />
          </Avatar> */}
          <Typography component="h1" variant="h5">
            Register
          </Typography>
          <Box
            component="form"
            noValidate
            onSubmit={handleRegistration}
            sx={{ mt: 3 }}
          >
            <Grid container spacing={2}>
              <Grid item xs={12}>
                <TextField
                  name="itemname"
                  required
                  fullWidth
                  id="itemname"
                  label="ItemName"
                  autoFocus
                  onChange={handleitemname}
                />
              </Grid>
              <Grid item xs={12}>
                <TextField
                  required
                  fullWidth
                  id="price"
                    label="Price"
                    name="price"
                    autoComplete="price"
                    onChange={handleprice}
                />
              </Grid>
              <Grid item xs={12}>
                <TextField
                  required
                  fullWidth
                  name="password"
                  label="Password"
                  type="password"
                  id="password"
                  autoComplete="new-password"
                  onChange={handlepassword}
                />
              </Grid>
              <Grid item xs={12}>
                <TextField
                  required
                  fullWidth
                  type="number"
                  name="age"
                  label="Age"
                  id="age"
                  onChange={handleage}
                />
              </Grid>
              <Grid item xs={12}>
                <FormControl fullWidth>
                  {/* <TextField
                  required
                  fullWidth
                  name="batch"
                  label="Batch"
                  type="batch"
                  id="batch"
                  helperText="Ex. UG1, UG2, PG1"
                /> */}
                  <InputLabel id="batch-select-label" required>
                    Batch
                  </InputLabel>
                  <Select
                    fullWidth
                    labelId="batch-select-label"
                    id="demo-batch-select"
                    name="batch"
                    id="batch"
                    // value={buyer ? 'buyer' : vendor ? 'vendor' : 'Select'}
                    value={batch}
                    label="Batch"
                    onChange={handlebatch}
                  >
                    <MenuItem value={0}>UG1</MenuItem>
                    <MenuItem value={1}>UG2</MenuItem>
                    <MenuItem value={2}>UG3</MenuItem>
                    <MenuItem value={3}>UG4</MenuItem>
                    <MenuItem value={4}>UG5</MenuItem>
                    <MenuItem value={5}>PG1</MenuItem>
                    <MenuItem value={6}>PG2</MenuItem>
                  </Select>
                </FormControl>
              </Grid>
              <Grid item xs={12}>
                <TextField
                  required
                  fullWidth
                  type="number"
                  id="contact"
                  label="Contact Number"
                  name="contact"
                  onChange={handlecontact}
                  error={!contactbool}
                  helperText={
                    !contactbool ? "Enter a valid contact number" : ""
                  }
                />
              </Grid>
            </Grid>
            <Button
              type="submit"
              fullWidth
              variant="contained"
              sx={{ mt: 3, mb: 2 }}
            >
              Register
            </Button>
          </Box>
        </Box>
        {/* <Copyright sx={{ mt: 5 }} /> */}
      </Container>
    </ThemeProvider>
  );
}
