import * as React from 'react';
import List from '@mui/material/List';
import ListItem from '@mui/material/ListItem';  
import ListItemText from '@mui/material/ListItemText';
import Divider from '@mui/material/Divider';
import ls from "local-storage";
import Box from "@mui/material/Box";
import axios from 'axios';
import { useState } from 'react';
import { useEffect } from 'react';
import { Button, TextField } from '@mui/material';
import Stack from '@mui/material/Stack';

const style = {
  width: '100%',
  maxWidth: 360,
  bgcolor: 'background.paper',
};

export default function MyProfile() {
     const [name, setName] = React.useState("");
     const [managername, setManagername] = React.useState("");
     const [shopname, setShopname] = React.useState("");
     const [email, setEmail] = React.useState("");
     const [contact, setContact] = React.useState("");
     const [age, setAge] = React.useState("");
     const [batch, setBatch] = React.useState("");
     const [opentime, setOpentime] = React.useState("");
     const [closetime, setClosetime] = React.useState("");

     const [edit, setEdit] = React.useState(false);

     const handleedit = () => {
          setEdit(true);
      };

      const handlesave = () => {
          setEdit(false);
          if(ls.get("usertype") === 1) {
            axios.post('http://localhost:4000/user/buyerupdate', {
                name: name,
                email: email,
                contact: contact,
                age: age,
                batch: batch,
          })
          .then(function (response) {
              console.log(response);
          })
          .catch(function (error) {
              console.log(error);
          });
          }
          else if(ls.get("usertype") === 2) {
            axios.post('http://localhost:4000/user/vendorupdate', {
                managername: managername,
                shopname: shopname,
                email: email,
                contact: contact,
                opentime: opentime,
                closetime: closetime,
          })
          .then(function (response) {
              console.log(response);
          })
          .catch(function (error) {
              console.log(error);
          });
          }
      };


    //Use email from local storage top get necessary info from backend
    useEffect(() => {
        {
            ls.get("usertype") === 1 ?
            axios.post("http://localhost:4000/user/buyerdetails", {
                email: ls.get("email")
            })
            .then((response) => {
                setName(response.data.name);
                setEmail(response.data.email);
                setContact(response.data.contact);
                setAge(response.data.age);
                setBatch(response.data.batch);
            })
            .catch(function (error) {
                console.log(error);
            })
            :
            axios.post("http://localhost:4000/user/vendordetails", {
                email: ls.get("email")
            })
            .then((response) => {
                setManagername(response.data.managername);
                setShopname(response.data.shopname);
                setEmail(response.data.email);
                setContact(response.data.contact);
                setOpentime(response.data.opentime);
                setClosetime(response.data.closetime);
            })
            .catch(function (error) {
                console.log(error);
            })
        }
    }, []);

  return (
    <List sx={style} component="nav" aria-label="mailbox folders">
    { ls.get("usertype") === 1 ?
    <Box>
      <ListItem button>
        <ListItemText primary="Name:" />
        <TextField 
            id="outlined-basic"
            variant="outlined"
            value={name}
            InputProps={{
                readOnly: true,
            }}
        />
      </ListItem>
      <Divider />
      <ListItem button>
        <ListItemText primary="Email:" />
        <TextField
            id="outlined-basic"
            variant="outlined"
            value={email}
            InputProps={{
              readOnly: true,
            }}
        />
      </ListItem>
      <Divider />
      <ListItem button>
        <ListItemText primary="Contact:" />
        <TextField
            id="outlined-basic"
            variant="outlined"
            value={contact}
            InputProps={{
              readOnly: true,
            }}
        />
      </ListItem>
      <Divider />
      <ListItem button>
        <ListItemText primary="Age" />
        <TextField
            id="outlined-basic"
            variant="outlined"
            value={age}
            InputProps={{
              readOnly: true,
            }}
        />
      </ListItem>
      <Divider />
      <ListItem button>
        <ListItemText primary="Batch" />
        <TextField
            id="outlined-basic"
            variant="outlined"
            value={batch}
            InputProps={{
              readOnly: true,
            }}
        />
      </ListItem>
      <Divider light />
      <br/>
      <br/>
      <br/>
      <Stack
        spacing={5}
        direction="row"
        >
      <Button
        variant="contained"
        color="primary"
        disabled = {edit}
        >
        Edit Profile
        </Button>
        <Button
        variant="contained"
        color="primary"
        disabled = {!edit}
        >
          Save Profile
        </Button>
      </Stack>
    </Box>
    :
    <Box>
      <ListItem button>
        <ListItemText primary="Manager Name:"/>
        <TextField 
            id="outlined-basic"
            variant="outlined"
            value={managername}
            InputProps={{
                readOnly: true,
            }}
        />
      </ListItem>
      <Divider />
      <ListItem button>
        <ListItemText primary="Shop Name:" />
        <TextField
            id="outlined-basic"
            variant="outlined"
            value={shopname}
            InputProps={{
                readOnly: true,
            }}
        />
      </ListItem>
      <Divider />
      <ListItem button>
        <ListItemText primary="Email:" />
        <TextField
            id="outlined-basic"
            variant="outlined"
            value={email}
            InputProps={{
                readOnly: true,
            }}
        />
      </ListItem>
      <Divider />
      <ListItem button>
        <ListItemText primary="Contact:" />
        <TextField
            id="outlined-basic"
            variant="outlined"
            value={contact}
            InputProps={{
                readOnly: true,
            }}
        />
      </ListItem>
      <Divider />
      <ListItem button>
        <ListItemText primary="Open Time:" />
        <TextField
            id="outlined-basic"
            variant="outlined"
            value={opentime}
            InputProps={{
                readOnly: true,
            }}
        />
      </ListItem>
      <Divider />
      <ListItem button>
        <ListItemText primary="Close Time:" />
        <TextField
            id="outlined-basic"
            variant="outlined"
            value={closetime}
            InputProps={{
                readOnly: true,
            }}
        />
      </ListItem>
      <Divider light />
      <br/>
      <br/>
      <br/>
      <Stack
        spacing={5}
        direction="row"
        >
      <Button
        variant="contained"
        color="primary"
        disabled = {edit}
        >
        Edit Profile
        </Button>
        <Button
        variant="contained"
        color="primary"
        disabled = {!edit}
        >
          Save Profile
        </Button>
      </Stack>
    </Box>
    }
    </List>
  );
}
