import * as React from 'react';
import List from '@mui/material/List';
import ListItem from '@mui/material/ListItem';  
import ListItemText from '@mui/material/ListItemText';
import Divider from '@mui/material/Divider';
import ls from "local-storage";
import Box from "@mui/material/Box";
import axios from 'axios';
import { useState } from 'react';
import { useEffect } from 'react';
import { Button, ListSubheader, TextField } from '@mui/material';
import Stack from '@mui/material/Stack';


export default function VStatistics() {
    const [refresh, setRefresh] = useState(false);
    const [placedorders, setPlacedOrders] = useState([]);
    const [pendingorders, setPendingOrders] = useState([]);
    const [completeorders, setCompleteOrders] = useState([]);
    const [array_5, setArray_5] = useState(['', '', '', '', '']);
    useEffect(() => {
        axios.post('http://localhost:4000/vendor/statistics', {
            email: ls.get("email")
        })
        .then(function (response) {
            console.log(response);
            setPlacedOrders(response.data.placedorders);
            setPendingOrders(response.data.pendingorders);
            setCompleteOrders(response.data.completeorders);
            setArray_5[0] = response.data.item[0];
            setArray_5[1] = response.data.item[1];
            setArray_5[2] = response.data.item[2];
            setArray_5[3] = response.data.item[3];
            setArray_5[4] = response.data.item[4];

        })
        .catch(function (error) {
            console.log(error);
        });
    }
    , []);

    return (
        <div>
            <Box sx={{ flexGrow: 1 }}>
                <List>
                    <ListSubheader><center><h3>Most ordered food items</h3></center></ListSubheader>
                    <Divider />
                    <ListItem>
                        <ListItemText primary="Item 1" secondary={array_5[0]} />
                        <ListItemText primary="Item 2" secondary={array_5[1]} />
                        <ListItemText primary="Item 3" secondary={array_5[2]} />
                        <ListItemText primary="Item 4" secondary={array_5[3]} />
                        <ListItemText primary="Item 5" secondary={array_5[4]} />
                    </ListItem>
                    <Divider />
                </List>
                </Box>
            <Box p={2}>
                <Stack>
                    <List>
                        <ListItem>
                            <ListItemText primary="Orders Placed:" secondary={placedorders} />
                        </ListItem>
                        <Divider />
                        <ListItem>
                            <ListItemText primary="Orders Pending:" secondary={pendingorders} />
                        </ListItem>
                        <Divider />
                        <ListItem>
                            <ListItemText primary="Orders Completed:" secondary={completeorders} />
                        </ListItem>
                        <Divider />
                    </List>
                </Stack>
            </Box>
        </div>
    );
}
