import * as React from 'react';
import Table from '@mui/material/Table';
import TableBody from '@mui/material/TableBody';
import TableCell from '@mui/material/TableCell';
import TableContainer from '@mui/material/TableContainer';
import TableHead from '@mui/material/TableHead';
import TableRow from '@mui/material/TableRow';
import Paper from '@mui/material/Paper';
import { useEffect, useState } from 'react';
import ls from 'local-storage';
import axios from 'axios';
import { Button } from '@mui/material';
import Box from "@mui/material/Box";
import Stack from '@mui/material/Stack';


function createData(ItemName, Price, VNV, Tags, AddOns) {
  return {ItemName, Price, VNV, Tags, AddOns};
}

const initialrows = [
  createData('Frozen yoghurt', 159, 6.0, 'Me', 24),
  createData('Frozen yoghurt', 159, 6.0, 'Me', 24),

];

export default function ShowMenu() {
    const [rows, setRows] = useState(initialrows);

    // Fill up the rows array with data.
    useEffect(() => {
        // Get all documents from the database for orders for the particular vendor
        axios.post('http://localhost:4000/vendor/fooditems', {
            manangeremail: ls.get('email'),
    })
        .then(function (response) {
            console.log(response);
            setRows(
                response.data.map(
                    (item) => createData(item.ItemName, item.Price, item.VNV, item.Tags, item.AddOns)
                )
            );
        })
        .catch(function (error) {
            console.log(error);
        });
    })

    const handleadditem = () => {
    } 


  return (
    <Box>
      <FormControl fullWidth>
        <InputLabel id="demo-simple-select-label">Buyer/Vendor</InputLabel>
        <Select
          labelId="demo-simple-select-label"
          id="demo-simple-select"
          // value={buyer ? 'buyer' : vendor ? 'vendor' : 'Select'}
          value={buyer===1 ? 'buyer' : vendor===1 ? 'vendor' : ''}
          label="Buyer/Vendor"
          onChange={handleChange}
        >
          <MenuItem value="buyer">Buyer</MenuItem>
          <MenuItem value="vendor">Vendor</MenuItem>
        </Select>
        {/* <NewFunc /> */}
      </FormControl>
    <Stack
              sx={{ pt: 4 }}
              direction="row"
              spacing={2}
              justifyContent="center"
            >
    <Button variant="contained">Add Item</Button>
    </Stack>

    <TableContainer component={Paper}>
      <Table sx={{ minWidth: 650 }} aria-label="simple table">
        <TableHead>
          <TableRow>
            <TableCell>ItemName</TableCell>
            <TableCell align="right">Price</TableCell>
            <TableCell align="right">Veg/Non-Veg</TableCell>
            <TableCell align="right">Tags</TableCell>
            <TableCell align="right">AddOns</TableCell>
            <TableCell align="right"></TableCell>
            <TableCell align="right"></TableCell>
          </TableRow>
        </TableHead>
        <TableBody>
          {rows.map((row) => (
            <TableRow
              key={row.name}
              sx={{ '&:last-child td, &:last-child th': { border: 0 } }}
            >
              <TableCell component="th" scope="row">
                {row.ItemName}
              </TableCell>
              <TableCell align="right">{row.Price}</TableCell>
              <TableCell align="right">{row.VNV}</TableCell>
              <TableCell align="right">{row.Tags}</TableCell>
              <TableCell align="right">{row.AddOns}</TableCell>
                <TableCell align="right">
                    <Button variant="contained">Edit</Button>
                </TableCell>

                    <TableCell align="right">
                        <Button variant="contained">Delete</Button>
                    </TableCell>

            </TableRow>
          ))}
        </TableBody>
      </Table>
    </TableContainer>
    </Box>
  );
}
